import https from "https";
import http from "http";
import type { Express, Request, Response } from "express";

export function setupRadioProxy(app: Express) {
  // Radio stream proxy to handle CORS and format issues
  app.get("/api/radio-stream", (req: Request, res: Response) => {
    // Get the station URL from query parameter, fallback to default streams
    const requestedStream = req.query.url as string;

    // Station-specific fallback URLs with unique streams per station
    const stationFallbacks: { [key: string]: string[] } = {
      "https://ice1.somafm.com/beatblender-128-mp3": [
        "https://ice1.somafm.com/beatblender-128-mp3",
        "https://ice2.somafm.com/beatblender-128-mp3",
        "https://ice6.somafm.com/beatblender-128-mp3",
      ],
      "https://ice1.somafm.com/groovesalad-256-mp3": [
        "https://ice1.somafm.com/groovesalad-256-mp3",
        "https://ice2.somafm.com/groovesalad-256-mp3",
        "https://ice6.somafm.com/groovesalad-256-mp3",
      ],
      "https://ice1.somafm.com/spacestation-128-mp3": [
        "https://ice1.somafm.com/spacestation-128-mp3",
        "https://ice2.somafm.com/spacestation-128-mp3",
        "https://ice6.somafm.com/spacestation-128-mp3",
      ],
      "https://ice1.somafm.com/metal-128-mp3": [
        "https://ice1.somafm.com/metal-128-mp3",
        "https://ice2.somafm.com/metal-128-mp3",
        "https://ice6.somafm.com/metal-128-mp3",
      ],
    };

    const streamUrls = requestedStream
      ? stationFallbacks[requestedStream] || [
          requestedStream,
          requestedStream +
            (requestedStream.includes("?") ? "&" : "?") +
            "nocache=" +
            Date.now(),
          requestedStream.replace(".aac", ".mp3"),
          // Final fallback
          "https://ice1.somafm.com/metal-128-mp3",
        ]
      : [
          // Default streams if no URL provided
          "https://ice1.somafm.com/beatblender-128-mp3",
          "https://ice1.somafm.com/groovesalad-256-mp3",
          "https://ice1.somafm.com/metal-128-mp3",
        ];

    let currentStreamIndex = 0;

    const tryNextStream = () => {
      if (currentStreamIndex >= streamUrls.length) {
        if (!res.headersSent) {
          res.status(503).json({ error: "All stream sources unavailable" });
        }
        return;
      }

      const streamUrl = streamUrls[currentStreamIndex];
      console.log(
        `Trying radio stream ${currentStreamIndex + 1}: ${streamUrl}`,
      );

      // Choose the appropriate module based on protocol
      const isHttps = streamUrl.startsWith("https://");
      const requestModule = isHttps ? https : http;

      const request = requestModule.get(streamUrl, (response) => {
        // Handle redirects for streaming services
        if (
          (response.statusCode === 301 || response.statusCode === 302) &&
          response.headers.location
        ) {
          console.log(
            `Stream ${currentStreamIndex + 1} redirected to: ${response.headers.location}`,
          );
          const redirectUrl = response.headers.location;
          const redirectModule = redirectUrl.startsWith("https://")
            ? https
            : http;

          const redirectRequest = redirectModule.get(
            redirectUrl,
            (redirectResponse) => {
              if (redirectResponse.statusCode === 200 && !res.headersSent) {
                // Set headers for audio streaming
                res.setHeader("Content-Type", "audio/mpeg");
                res.setHeader("Access-Control-Allow-Origin", "*");
                res.setHeader("Access-Control-Allow-Methods", "GET");
                res.setHeader("Cache-Control", "no-cache");
                res.setHeader("Accept-Ranges", "bytes");

                // Pipe the stream directly
                redirectResponse.pipe(res);
                console.log(
                  `Stream ${currentStreamIndex + 1} connected successfully via redirect`,
                );

                redirectResponse.on("error", (error) => {
                  console.error(
                    `Redirect stream ${currentStreamIndex + 1} error:`,
                    error,
                  );
                  if (!res.headersSent) {
                    currentStreamIndex++;
                    tryNextStream();
                  }
                });
              } else {
                console.log(
                  `Redirect stream ${currentStreamIndex + 1} failed with status ${redirectResponse.statusCode}`,
                );
                currentStreamIndex++;
                tryNextStream();
              }
            },
          );

          redirectRequest.on("error", (error) => {
            console.error(
              `Redirect request ${currentStreamIndex + 1} error:`,
              error,
            );
            currentStreamIndex++;
            tryNextStream();
          });

          redirectRequest.setTimeout(10000, () => {
            console.log(`Redirect stream ${currentStreamIndex + 1} timeout`);
            redirectRequest.destroy();
            currentStreamIndex++;
            tryNextStream();
          });
        } else if (response.statusCode === 200 && !res.headersSent) {
          // Set headers for audio streaming
          res.setHeader("Content-Type", "audio/mpeg");
          res.setHeader("Access-Control-Allow-Origin", "*");
          res.setHeader("Access-Control-Allow-Methods", "GET");
          res.setHeader("Cache-Control", "no-cache");
          res.setHeader("Accept-Ranges", "bytes");

          // Pipe the stream directly
          response.pipe(res);
          console.log(
            `Stream ${currentStreamIndex + 1} connected successfully`,
          );

          response.on("error", (error) => {
            console.error(`Stream ${currentStreamIndex + 1} error:`, error);
            if (!res.headersSent) {
              currentStreamIndex++;
              tryNextStream();
            }
          });
        } else {
          console.log(
            `Stream ${currentStreamIndex + 1} failed with status ${response.statusCode}`,
          );
          currentStreamIndex++;
          tryNextStream();
        }
      });

      request.on("error", (error) => {
        console.error(`Stream ${currentStreamIndex + 1} request error:`, error);
        currentStreamIndex++;
        tryNextStream();
      });

      request.setTimeout(10000, () => {
        console.log(`Stream ${currentStreamIndex + 1} timeout`);
        request.destroy();
        currentStreamIndex++;
        tryNextStream();
      });
    };

    tryNextStream();
  });
}

server/recaptcha.ts.content:
import { RecaptchaEnterpriseServiceClient } from "@google-cloud/recaptcha-enterprise";

export interface RecaptchaAssessmentRequest {
  token: string;
  siteKey: string;
  accountId?: string;
  phoneNumber?: string;
  action?: string;
}

export interface RecaptchaAssessmentResult {
  valid: boolean;
  score: number;
  reasons: string[];
  phoneRisk?: {
    level: "LOW" | "MEDIUM" | "HIGH";
    reasons: string[];
  };
}

class RecaptchaService {
  private client: RecaptchaEnterpriseServiceClient | null = null;
  private projectId: string;

  constructor() {
    this.projectId = process.env.GOOGLE_CLOUD_PROJECT_ID || "";

    if (process.env.GOOGLE_APPLICATION_CREDENTIALS) {
      this.client = new RecaptchaEnterpriseServiceClient();
    }
  }

  async assessSMSDefense(
    request: RecaptchaAssessmentRequest,
  ): Promise<RecaptchaAssessmentResult> {
    if (!this.client || !this.projectId) {
      console.warn("reCAPTCHA Enterprise not configured");
      return {
        valid: true, // Allow requests when not configured
        score: 0.5,
        reasons: ["Service not configured"],
      };
    }

    try {
      const projectPath = this.client.projectPath(this.projectId);

      const assessment = {
        event: {
          token: request.token,
          siteKey: request.siteKey,
          userInfo: {
            accountId: request.accountId || "",
            userIds: request.phoneNumber
              ? [
                  {
                    phoneNumber: request.phoneNumber,
                  },
                ]
              : [],
          },
        },
      };

      const [response] = await this.client.createAssessment({
        parent: projectPath,
        assessment,
      });

      const tokenProperties = response.tokenProperties;
      const riskAnalysis = response.riskAnalysis;
      const fraudSignals = response.fraudSignals;

      // Validate token
      if (!tokenProperties?.valid) {
        return {
          valid: false,
          score: 0,
          reasons: tokenProperties?.invalidReason
            ? [(tokenProperties.invalidReason as any)]
            : ["Invalid token"],
        };
      }

      // Check action matches
      if (request.action && tokenProperties.action !== request.action) {
        return {
          valid: false,
          score: 0,
          reasons: ["Action mismatch"],
        };
      }

      // Analyze phone number risk
      let phoneRisk;
      if (
        (fraudSignals as any)?.cardTestingSignals ||
        (fraudSignals as any)?.stolenInstrumentSignals
      ) {
        phoneRisk = {
          level: "HIGH",
          reasons: ["Fraud signals detected"],
        };
      }

      return {
        valid: true,
        score: riskAnalysis?.score || 0.5,
        reasons: (riskAnalysis?.reasons as any) || [],
        phoneRisk,
      };
    } catch (error) {
      console.error("reCAPTCHA assessment failed:", error);
      return {
        valid: false,
        score: 0,
        reasons: ["Assessment failed"],
      };
    }
  }

  async assessRegistration(
    request: RecaptchaAssessmentRequest,
  ): Promise<RecaptchaAssessmentResult> {
    return this.assessSMSDefense({
      ...request,
      action: "registration",
    });
  }

  async assessLogin(
    request: RecaptchaAssessmentRequest,
  ): Promise<RecaptchaAssessmentResult> {
    return this.assessSMSDefense({
      ...request,
      action: "login",
    });
  }
}

export const recaptchaService = new RecaptchaService();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!,
    );
  },
  { maxAge: 3600 * 1000 },
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(claims: any) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback,
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env.REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href,
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

