.gm-style-iw-t::after,
.gm-style-iw-t::before {
  background: transparent !important;
  box-shadow: none !important;
}

/* Aggressively remove all backgrounds and box-shadows from Google Maps InfoWindow */
.gm-style-iw,
.gm-style-iw-c,
.gm-style-iw-chr,
.gm-style-iw-ch,
.gm-style-iw-d,
.gm-style-iw-t,
.gm-style-iw-t::after,
.gm-style-iw-t::before,
.gm-style-iw button,
.gm-style-iw span,
.gm-style-iw * {
  background: transparent !important;
  box-shadow: none !important;
