/* Google Maps InfoWindow Overrides - Complete white background removal */
.gm-style-iw,
.gm-style-iw-c,
.gm-style-iw-d,
.gm-style-iw-t,
.gm-style-iw-t::after,
.gm-style-iw-t::before {
  background: transparent !important;
  border: none !important;
  box-shadow: none !important;
  padding: 0 !important;
  margin: 0 !important;
}

/* Remove all default Google Maps InfoWindow styling */
.gm-style-iw > div,
.gm-style-iw-c > div,
.gm-style-iw-d > div {
  background: transparent !important;
  border: none !important;
  box-shadow: none !important;
  padding: 0 !important;
  margin: 0 !important;
}

/* Remove InfoWindow arrow */
.gm-style-iw-t::after,
.gm-style-iw-t::before {
