}

/* Remove any remaining white backgrounds */
.gm-style-iw [style*="background: #fff"],
.gm-style-iw [style*="background: white"],
.gm-style-iw [style*="background-color: #fff"],
.gm-style-iw [style*="background-color: white"] {
  background: transparent !important;
}

/* Ensure our custom InfoWindow content is fully visible */
#custom-infowindow {
  background: var(--custom-bg) !important;
  border: var(--custom-border) !important;
  border-radius: 20px !important;
  box-shadow: var(--custom-shadow) !important;
  padding: 24px !important;
  margin: 0 !important;
  position: relative !important;
  z-index: 1000 !important;
}

/* Remove any Google Maps default InfoWindow container */
.gm-style-iw-c {
  background: transparent !important;
  border: none !important;
  box-shadow: none !important;
  padding: 0 !important;
  margin: 0 !important;
  max-width: none !important;
  min-width: none !important;
}

/* Remove InfoWindow close button default styling */
.gm-style-iw-d button,
.gm-style-iw-d .gm-style-iw-c button {
  display: none !important;
}

/* Ensure no white space around InfoWindow */
.gm-style-iw-d {
  background: transparent !important;
  border: none !important;
  box-shadow: none !important;
  padding: 0 !important;
  margin: 0 !important;
  overflow: visible !important;
}

/* Additional comprehensive overrides for all possible white backgrounds */
.gm-style-iw *,
.gm-style-iw-c *,
.gm-style-iw-d * {
  background: transparent !important;
}

/* Remove any remaining Google Maps default styling */
.gm-style-iw,
.gm-style-iw-c,
.gm-style-iw-d {
  background: transparent !important;
  border: none !important;
  box-shadow: none !important;
  padding: 0 !important;
  margin: 0 !important;
  max-width: none !important;
  min-width: none !important;
  width: auto !important;
  height: auto !important;
}

/* Force our custom InfoWindow to be the only visible element */
#custom-infowindow {
  display: block !important;
  visibility: visible !important;
  opacity: 1 !important;
  background: var(--custom-bg) !important;
  border: var(--custom-border) !important;
  border-radius: 20px !important;
  box-shadow: var(--custom-shadow) !important;
  padding: 24px !important;
  margin: 0 !important;
  position: relative !important;
  z-index: 1000 !important;
  min-width: 280px !important;
  max-width: 320px !important;
}

/* Focus styles for accessibility */

:focus-visible {
  outline: 2px solid hsl(var(--ring));
  outline-offset: 2px;
}

/* FLOATING PLAYER FOCUS RING PREVENTION */
/* Prevent focus rings on all elements inside floating player except the play button */
.floating-player-no-focus *:not(button):focus,
.floating-player-no-focus *:not(button):focus-visible,
.floating-player-no-focus .focus-safe-area *:focus,
.floating-player-no-focus .focus-safe-area *:focus-visible {
  outline: none !important;
  border-color: transparent !important;
  box-shadow: none !important;
  --tw-ring-color: transparent !important;
}

/* Allow focus only on the main play/pause button */
.floating-player-no-focus button:focus-visible {
  outline: 2px solid hsl(var(--ring)) !important;
  outline-offset: 2px !important;
  box-shadow: 0 0 0 2px hsl(var(--background)), 0 0 0 4px hsl(var(--ring) / 0.5) !important;
}

/* Remove focus from volume slider and other interactive elements */
.floating-player-no-focus input[type="range"]:focus,
.floating-player-no-focus input[type="range"]:focus-visible {
  outline: none !important;
  border: none !important;
  box-shadow: none !important;
  --tw-ring-color: transparent !important;
}

/* Enhanced focus styles for better accessibility */
button:focus-visible,
input:focus-visible,
select:focus-visible,
textarea:focus-visible,
[role="button"]:focus-visible,
[tabindex]:focus-visible {
  outline: 2px solid hsl(var(--ring));
  outline-offset: 2px;
  box-shadow: 0 0 0 2px hsl(var(--background)), 0 0 0 4px hsl(var(--ring));
}

