/* Performance optimizations */

html {
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  text-rendering: optimizeLegibility;
  scroll-behavior: smooth;
}

body {
  font-display: swap;
}

/* Hardware acceleration for smooth animations */

.smooth-animation {
  will-change: transform;
  transform: translateZ(0);
}

/* Reduce layout shifts */

img,
video {
  height: auto;
  max-width: 100%;
}

/* Accessibility improvements */

.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border: 0;
}

/* Focus styles for accessibility */

.focus-visible {
  outline: 2px solid var(--color-primary);
  outline-offset: 2px;
}

/* Skip to content link - removed */

:root {
  /* Dynamic theme colors - will be updated by ThemeContext */
  --color-primary: #ff6b35;
  --color-secondary: #d32f2f;
  --color-accent: #ff9800;
  --color-background: #000000;
  --color-surface: #0a0a0a;
  --color-text: #ffffff;
  --color-text-secondary: #cccccc;
  --color-border: #333333;
  --gradient-primary: linear-gradient(135deg, #ff6b35 0%, #d32f2f 100%);
  /* Shadcn variables using theme colors */
  --background: var(--color-background);
  --foreground: var(--color-text);
  --muted: var(--color-surface);
  --muted-foreground: var(--color-text-secondary);
  --popover: var(--color-surface);
  --popover-foreground: var(--color-text);
  --card: var(--color-surface);
  --card-foreground: var(--color-text);
  --border: var(--color-border);
  --input: var(--color-surface);
  --primary: var(--color-primary);
  --primary-foreground: var(--color-text);
  --secondary: var(--color-secondary);
  --secondary-foreground: var(--color-text);
  --accent: var(--color-accent);
  --accent-foreground: var(--color-text);
  --destructive: hsl(0, 73%, 57%);
  --destructive-foreground: hsl(0, 0%, 98%);
  --ring: var(--color-accent);
  --radius: 0.75rem;
  /* Legacy metal theme colors for compatibility */
  --metal-orange: var(--color-primary);
  --metal-gold: var(--color-accent);
  --metal-red: var(--color-secondary);
  --metal-blue: hsl(210, 100%, 65%);
}

