import { useState, useEffect } from "react";
import { Dialog, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import * as DialogPrimitive from "@radix-ui/react-dialog";
import { X } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useAuth } from "@/contexts/AuthContext";
import { useFirebaseAuth } from "@/contexts/FirebaseAuthContext";
import { useTheme } from "@/contexts/ThemeContext";
import { useToast } from "@/hooks/use-toast";
import {
  Loader2,
  Mail,
  Lock,
  User,
  LogIn,
  UserPlus,
  Phone,
} from "lucide-react";
import GoogleLogoPath from "@assets/GoogleLogoIcon.png";

interface AuthModalProps {
  isOpen: boolean;
  onClose: () => void;
  initialMode?: "login" | "register";
}

export default function AuthModal({
  isOpen,
  onClose,
  initialMode = "login",
}: AuthModalProps) {
  const [mode, setMode] = useState<"login" | "register">(initialMode);

  // Update mode when initialMode prop changes
  useEffect(() => {
    setMode(initialMode);
  }, [initialMode]);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [username, setUsername] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [loading, setLoading] = useState(false);
  const [recaptchaToken, setRecaptchaToken] = useState("");

  const { isAuthenticated, signInWithGoogle } = useAuth();
  const { register: firebaseRegister, login: firebaseLogin, loginWithGoogle } = useFirebaseAuth();
  const { getColors } = useTheme();
  const { toast } = useToast();
  const colors = getColors();

  const handleGoogleAuth = async () => {
    try {
      setLoading(true);
      await signInWithGoogle();
      // The redirect will happen automatically
      onClose();
    } catch (error: any) {
      toast({
        title: "Sign In Failed",
        description: error.message || "Failed to sign in with Google.",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  // Phone number formatting
  const formatPhoneNumber = (value: string) => {
    const phoneNumber = value.replace(/[^\d]/g, "");
    const phoneNumberLength = phoneNumber?.length || 0;

    if (phoneNumberLength < 4) return phoneNumber;
    if (phoneNumberLength < 7) {
      return `(${phoneNumber.slice(0, 3)}) ${phoneNumber.slice(3)}`;
    }
    return `(${phoneNumber.slice(0, 3)}) ${phoneNumber.slice(3, 6)}-${phoneNumber.slice(6, 10)}`;
  };

  const handlePhoneChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const formatted = formatPhoneNumber(e.target.value);
    setPhoneNumber(formatted);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);

    try {
      if (mode === "login") {
        // Use Firebase Firestore login
        const result = await firebaseLogin(email, password);

        if (!result.success) {
          throw new Error(result.message || "Login failed");
        }

        toast({
          title: "Welcome back!",
          description: "You've successfully logged in with Firebase Firestore.",
        });

        // Close modal and refresh to update authentication state
        onClose();
        window.location.reload();
      } else {
        // Use Firebase Firestore registration
        const result = await firebaseRegister({
          firstName,
          lastName,
          email,
          phoneNumber: phoneNumber.replace(/[^\d]/g, ""),
          password,
        });

        if (!result.success) {
          throw new Error(result.message || "Registration failed");
        }

        toast({
          title: "Account created!",
          description: `Welcome to Spandex Salvation Radio! User Key: ${result.userKey}`,
        });

        // Close modal and refresh to update authentication state
        onClose();
        window.location.reload();
      }
      resetForm();
    } catch (error: any) {
      toast({
        title: mode === "login" ? "Login failed" : "Registration failed",
        description: error.message || "Please try again.",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const resetForm = () => {
    setEmail("");
    setPassword("");
    setUsername("");
    setFirstName("");
    setLastName("");
    setPhoneNumber("");
  };

  const switchMode = () => {
    setMode(mode === "login" ? "register" : "login");
    resetForm();
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogPrimitive.Portal>
        <DialogPrimitive.Overlay className="fixed inset-0 bg-black/80 data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0" style={{ zIndex: 9999 }} />
        <DialogPrimitive.Content
          className="fixed left-[50%] top-[50%] grid w-full max-w-md translate-x-[-50%] translate-y-[-50%] gap-4 p-6 shadow-lg duration-200 data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0 data-[state=closed]:zoom-out-95 data-[state=open]:zoom-in-95 data-[state=closed]:slide-out-to-left-1/2 data-[state=closed]:slide-out-to-top-[48%] data-[state=open]:slide-in-from-left-1/2 data-[state=open]:slide-in-from-top-[48%] sm:rounded-lg backdrop-blur-md"
          style={{
            backgroundColor: "rgba(0, 0, 0, 0.95)",
            borderColor: colors.primary + "40",
            border: "1px solid",
            zIndex: 10000
          }}
        >
          {/* Custom Close Button */}
          <DialogPrimitive.Close
            className="absolute right-4 top-4 rounded-full opacity-70 transition-all duration-200 hover:opacity-100 focus:outline-none disabled:pointer-events-none flex items-center justify-center"
            style={{
              color: colors.text,
              backgroundColor: "transparent",
              border: "none",
              width: "32px",
              height: "32px",
            }}
            onMouseEnter={(e) => {
              e.currentTarget.style.color = colors.primary;
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.color = colors.text;
            }}
          >
            <X className="h-4 w-4" />
            <span className="sr-only">Close</span>
          </DialogPrimitive.Close>

          <DialogHeader>
            <DialogTitle
              className="flex items-center gap-2"
              style={{ color: colors.text }}
            >
              {mode === "login" ? (
                <>
                  <LogIn
                    className="h-5 w-5"
                    style={{ color: colors.primary }}
                  />
                  Sign In
                </>
              ) : (
                <>
                  <UserPlus
                    className="h-5 w-5"
                    style={{ color: colors.primary }}
                  />
                  Create Account
                </>
              )}
            </DialogTitle>
          </DialogHeader>

          <form onSubmit={handleSubmit} className="space-y-4">
            {mode === "register" && (
              <>
                <div className="space-y-2">
                  <Label htmlFor="username" style={{ color: colors.text }}>
                    Username
                  </Label>
                  <div className="relative">
                    <User className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                    <Input
                      id="username"
                      value={username}
                      onChange={(e) => setUsername(e.target.value)}
                      className="pl-10 bg-black/50   placeholder:text-gray-400 placeholder:opacity-50"
                      style={{
                        color: colors.text,
                        borderColor: "#374151",
                      }}
                      onFocus={(e) =>
                        (e.currentTarget.style.borderColor = colors.primary)
                      }
                      onBlur={(e) =>
                        (e.currentTarget.style.borderColor = "#374151")
                      }
                      placeholder="Username"
                      required
                    />
                  </div>
                </div>
                <div className="grid grid-cols-2 gap-3">
                  <div className="space-y-2">
                    <Label htmlFor="firstName" style={{ color: colors.text }}>
                      First Name
                    </Label>
                    <div className="relative">
                      <User className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                      <Input
                        id="firstName"
                        value={firstName}
                        onChange={(e) => setFirstName(e.target.value)}
                        className="pl-10 bg-black/50   placeholder:text-gray-400 placeholder:opacity-50"
                        style={{
                          color: colors.text,
                          borderColor: "#374151",
                        }}
                        onFocus={(e) =>
                          (e.currentTarget.style.borderColor = colors.primary)
                        }
                        onBlur={(e) =>
                          (e.currentTarget.style.borderColor = "#374151")
                        }
                        placeholder="John"
                        required
                      />
                    </div>
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="lastName" style={{ color: colors.text }}>
                      Last Name
                    </Label>
                    <div className="relative">
                      <User className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                      <Input
                        id="lastName"
                        value={lastName}
                        onChange={(e) => setLastName(e.target.value)}
                        className="pl-10 bg-black/50   placeholder:text-gray-400 placeholder:opacity-50"
                        style={{
                          color: colors.text,
                          borderColor: "#374151",
                        }}
                        onFocus={(e) =>
                          (e.currentTarget.style.borderColor = colors.primary)
                        }
                        onBlur={(e) =>
                          (e.currentTarget.style.borderColor = "#374151")
                        }
                        placeholder="Doe"
                        required
                      />
                    </div>
                  </div>
                </div>
                <div className="space-y-2">
                  <Label htmlFor="phoneNumber" style={{ color: colors.text }}>
                    Phone Number
                  </Label>
                  <div className="relative">
                    <Phone className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                    <Input
                      id="phoneNumber"
                      type="tel"
                      value={phoneNumber}
                      onChange={handlePhoneChange}
                      className="pl-10 bg-black/50   placeholder:text-gray-400 placeholder:opacity-50"
                      style={{
                        color: colors.text,
                        borderColor: "#374151",
                      }}
                      onFocus={(e) =>
                        (e.currentTarget.style.borderColor = colors.primary)
                      }
                      onBlur={(e) =>
                        (e.currentTarget.style.borderColor = "#374151")
                      }
                      placeholder="+1 (111) 111-1111"
                      required
                    />
                  </div>
                </div>
              </>
            )}

            <div className="space-y-2">
              <Label htmlFor="email" style={{ color: colors.text }}>
                Email
              </Label>
              <div className="relative">
                <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                <Input
                  id="email"
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  className="pl-10 bg-black/50   placeholder:text-gray-400 placeholder:opacity-50"
                  style={{
                    color: colors.text,
                    borderColor: "#374151",
                  }}
                  onFocus={(e) =>
                    (e.currentTarget.style.borderColor = colors.primary)
                  }
                  onBlur={(e) =>
                    (e.currentTarget.style.borderColor = "#374151")
                  }
                  placeholder="johndoe@email.com"
                  required
                />
              </div>
            </div>

            <div className="space-y-2">
              <Label htmlFor="password" style={{ color: colors.text }}>
                Password
              </Label>
              <div className="relative">
                <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                <Input
                  id="password"
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="pl-10 bg-black/50   placeholder:text-gray-400 placeholder:opacity-50"
                  style={{
                    color: colors.text,
                    borderColor: "#374151",
                  }}
                  onFocus={(e) =>
                    (e.currentTarget.style.borderColor = colors.primary)
                  }
                  onBlur={(e) =>
                    (e.currentTarget.style.borderColor = "#374151")
                  }
                  placeholder="Password"
                  autoComplete={mode === "login" ? "current-password" : "new-password"}
                  required
                />
              </div>
            </div>

            <Button
              type="submit"
              className="w-full text-white"
              style={{
                backgroundColor: colors.primary,
              }}
              onMouseEnter={(e) =>
                (e.currentTarget.style.backgroundColor = colors.secondary)
              }
              onMouseLeave={(e) =>
                (e.currentTarget.style.backgroundColor = colors.primary)
              }
              disabled={loading}
            >
              {loading ? (
                <>
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  {mode === "login" ? "Logging In..." : "Creating Account..."}
                </>
              ) : mode === "login" ? (
                "Login"
              ) : (
                "Create Account"
              )}
            </Button>

            <div className="relative">
              <div className="absolute inset-0 flex items-center">
                <span className="w-full border-t border-gray-600" />
              </div>
              <div className="relative flex justify-center text-xs uppercase">
                <span className="bg-black px-2 text-gray-400">
                  Or continue with
                </span>
              </div>
            </div>

            <Button
              type="button"
              variant="outline"
              onClick={handleGoogleAuth}
              className="w-full bg-white text-black border-gray-300 hover:bg-gray-50"
              disabled={loading}
            >
              <img src={GoogleLogoPath} alt="Google" className="mr-2 h-4 w-4" />
              {mode === "login"
                ? "Continue with Google"
                : "Sign Up with Google"}
            </Button>

            <div className="text-center">
              <button
                type="button"
                onClick={switchMode}
                className="text-sm transition-colors duration-200"
                style={{ color: colors.primary }}
                onMouseEnter={(e) =>
                  (e.currentTarget.style.color = colors.secondary)
                }
                onMouseLeave={(e) =>
                  (e.currentTarget.style.color = colors.primary)
                }
              >
                {mode === "login"
                  ? "Don't have an account? Sign up"
                  : "Already have an account? Sign in"}
              </button>
            </div>
          </form>
        </DialogPrimitive.Content>
      </DialogPrimitive.Portal>
    </Dialog>
  );
}

client/src/components/AvatarSelector.tsx.content:
import { useState, useRef } from "react";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Upload, User, Camera } from "lucide-react";
import { useTheme } from "@/contexts/ThemeContext";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

interface AvatarSelectorProps {
  isOpen: boolean;
  onClose: () => void;
  currentAvatar?: string;
  onAvatarUpdate: (avatarUrl: string) => void;
}

// Pre-made avatar options (these would be stored in Firebase storage)
const AVATAR_OPTIONS = [
  { id: "metal-1", url: "/avatars/metal-skull.png", name: "Metal Skull" },
  { id: "metal-2", url: "/avatars/metal-guitar.png", name: "Guitar Hero" },
  { id: "metal-3", url: "/avatars/metal-horns.png", name: "Metal Horns" },
  { id: "metal-4", url: "/avatars/metal-flames.png", name: "Flaming Metal" },
  { id: "rock-1", url: "/avatars/rock-star.png", name: "Rock Star" },
  { id: "rock-2", url: "/avatars/rock-band.png", name: "Band Member" },
  { id: "vintage-1", url: "/avatars/vintage-vinyl.png", name: "Vinyl Lover" },
  { id: "vintage-2", url: "/avatars/vintage-radio.png", name: "Radio Head" },
  {
    id: "spandex-1",
    url: "/avatars/spandex-warrior.png",
    name: "Spandex Warrior",
  },
  { id: "spandex-2", url: "/avatars/spandex-legend.png", name: "Metal Legend" },
];

export default function AvatarSelector({
  isOpen,
  onClose,
  currentAvatar,
  onAvatarUpdate,
}: AvatarSelectorProps) {
  const [selectedAvatar, setSelectedAvatar] = useState(currentAvatar || "");
  const [uploading, setUploading] = useState(false);
  const [saving, setSaving] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const { colors } = useTheme();
  const { toast } = useToast();

  const handleFileUpload = async (
    event: React.ChangeEvent<HTMLInputElement>,
  ) => {
    const file = event.target.files?.[0];
    if (!file) return;

    // Validate file type
    if (!file.type.startsWith("image/")) {
      toast({
        title: "Invalid File",
        description: "Please select an image file.",
        variant: "destructive",
      });
      return;
    }

    // Validate file size (max 5MB)
    if (file.size > 5 * 1024 * 1024) {
      toast({
        title: "File Too Large",
        description: "Please select an image smaller than 5MB.",
        variant: "destructive",
      });
      return;
    }

    setUploading(true);

    try {
      const formData = new FormData();
      formData.append("avatar", file);

      const response = await fetch("/api/user/upload-avatar", {
        method: "POST",
        credentials: "include",
        body: formData,
      });

      if (!response.ok) {
        throw new Error("Upload failed");
      }

      const { avatarUrl } = await response.json();
      setSelectedAvatar(avatarUrl);

      toast({
        title: "Upload Successful",
        description: "Your profile image has been uploaded.",
      });
    } catch (error: any) {
      toast({
        title: "Upload Failed",
        description:
          error.message || "Failed to upload image. Please try again.",
        variant: "destructive",
      });
    } finally {
      setUploading(false);
    }
  };

  const handleSave = async () => {
    if (!selectedAvatar) return;

    setSaving(true);

    try {
      await apiRequest("POST", "/api/user/update-avatar", {
        profileImageUrl: selectedAvatar,
      });

      onAvatarUpdate(selectedAvatar);
      toast({
        title: "Avatar Updated",
        description: "Your profile avatar has been updated successfully.",
      });
      onClose();
    } catch (error: any) {
      toast({
        title: "Update Failed",
        description:
          error.message || "Failed to update avatar. Please try again.",
        variant: "destructive",
      });
    } finally {
      setSaving(false);
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent
        className="sm:max-w-2xl max-h-[80vh] overflow-y-auto"
        style={{
          backgroundColor: colors.cardBackground,
          borderColor: colors.primary,
          color: colors.text,
        }}
      >
        <DialogHeader>
          <DialogTitle className="flex items-center gap-3 text-xl font-bold">
            <User className="h-6 w-6" style={{ color: colors.primary }} />
            Choose Your Avatar
          </DialogTitle>
        </DialogHeader>

        <Tabs defaultValue="avatars" className="w-full">
          <TabsList
            className="grid w-full grid-cols-2 mb-6"
            style={{ backgroundColor: colors.background }}
          >
            <TabsTrigger value="avatars" style={{ color: colors.text }}>
              Pre-made Avatars
            </TabsTrigger>
            <TabsTrigger value="upload" style={{ color: colors.text }}>
              Upload Custom
            </TabsTrigger>
          </TabsList>

          <TabsContent value="avatars">
            <div className="grid grid-cols-5 gap-4 mb-6">
              {AVATAR_OPTIONS.map((avatar) => (
                <div
                  key={avatar.id}
                  className={`relative cursor-pointer rounded-lg border-2 p-2 transition-all duration-200 hover:scale-105 ${
                    selectedAvatar === avatar.url
                      ? "border-primary"
                      : "border-gray-600"
                  }`}
                  style={{
                    borderColor:
                      selectedAvatar === avatar.url
                        ? colors.primary
                        : "#4B5563",
                  }}
                  onClick={() => setSelectedAvatar(avatar.url)}
                >
                  <div className="aspect-square rounded-lg overflow-hidden bg-gray-800">
                    <img
                      src={avatar.url}
                      alt={avatar.name}
                      className="w-full h-full object-cover"
                      onError={(e) => {
                        // Fallback for missing avatar images
                        const target = e.target as HTMLImageElement;
                        target.src = `https://ui-avatars.com/api/?name=${encodeURIComponent(avatar.name)}&background=f97316&color=fff&size=128`;
                      }}
                    />
                  </div>
                  <p
                    className="text-xs text-center mt-2 truncate"
                    style={{ color: colors.text }}
                  >
                    {avatar.name}
                  </p>
                  {selectedAvatar === avatar.url && (
                    <div
                      className="absolute -top-1 -right-1 w-5 h-5 rounded-full flex items-center justify-center"
                      style={{ backgroundColor: colors.primary }}
                    >
                      <span className="text-xs text-white">‚úì</span>
                    </div>
                  )}
                </div>
              ))}
            </div>
          </TabsContent>

          <TabsContent value="upload">
            <div className="space-y-6">
              <div className="text-center">
                <div
                  className="mx-auto w-32 h-32 rounded-full border-2 border-dashed border-gray-600 flex items-center justify-center cursor-pointer hover:border-primary transition-colors"
                  onClick={() => fileInputRef.current?.click()}
                  style={{
                    borderColor: selectedAvatar ? colors.primary : "#4B5563",
                  }}
                >
                  {selectedAvatar && selectedAvatar.startsWith("data:") ? (
                    <img
                      src={selectedAvatar}
                      alt="Uploaded avatar"
                      className="w-full h-full rounded-full object-cover"
                    />
                  ) : selectedAvatar &&
                    !selectedAvatar.startsWith("/avatars/") ? (
                    <img
                      src={selectedAvatar}
                      alt="Current avatar"
                      className="w-full h-full rounded-full object-cover"
                    />
                  ) : (
                    <div className="text-center">
                      <Camera
                        className="mx-auto h-8 w-8 mb-2"
                        style={{ color: colors.text }}
                      />
                      <p className="text-sm" style={{ color: colors.text }}>
                        {uploading ? "Uploading..." : "Click to upload"}
                      </p>
                    </div>
                  )}
                </div>

                <input
                  ref={fileInputRef}
                  type="file"
                  accept="image/*"
                  onChange={handleFileUpload}
                  className="hidden"
                  disabled={uploading}
                />

                <Button
                  type="button"
                  variant="outline"
                  onClick={() => fileInputRef.current?.click()}
                  disabled={uploading}
                  className="mt-4"
                  style={{ borderColor: colors.primary, color: colors.primary }}
                >
                  <Upload className="h-4 w-4 mr-2" />
                  {uploading ? "Uploading..." : "Choose File"}
                </Button>
              </div>

              <div
                className="text-center text-sm opacity-75"
                style={{ color: colors.text }}
              >
                <p>Supported formats: JPG, PNG, GIF</p>
                <p>Maximum size: 5MB</p>
                <p>Recommended: 200x200 pixels or larger</p>
              </div>
            </div>
          </TabsContent>
        </Tabs>

        <div
          className="flex justify-between pt-4 border-t"
          style={{ borderColor: "#374151" }}
        >
          <Button
            type="button"
            variant="ghost"
            onClick={onClose}
            style={{ color: colors.text }}
          >
            Cancel
          </Button>

          <Button
            type="button"
            onClick={handleSave}
            disabled={!selectedAvatar || saving}
            className="px-6"
            style={{
              backgroundColor: colors.primary,
              color: "white",
              opacity: !selectedAvatar || saving ? 0.6 : 1,
            }}
          >
            {saving ? "Saving..." : "Save Avatar"}
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/ChatButton.tsx.content:
import { useState } from "react";
import { MessageCircle, X, Crown, LogIn } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { useAuth } from "@/contexts/AuthContext";
import { useTheme } from "@/contexts/ThemeContext";

export default function ChatButton() {
  const [showNotification, setShowNotification] = useState(false);
  const { user, isAuthenticated } = useAuth();
  const { colors } = useTheme();

  // Check if user has paid subscription
  const hasPaidSubscription = (user as any)?.stripeSubscriptionId || false;

  const handleChatClick = () => {
    if (!isAuthenticated || !hasPaidSubscription) {
      setShowNotification(true);
    }
  };

  const handleSignIn = () => {
    // Trigger auth modal
    const event = new CustomEvent("openAuthModal", {
      detail: { mode: "login" },
    });
    window.dispatchEvent(event);
    setShowNotification(false);
  };

  const handleSubscribe = () => {
    document
      .getElementById("subscribe")
      ?.scrollIntoView({ behavior: "smooth" });
    setShowNotification(false);
  };

  // Only show chat button for authenticated users with active subscriptions
  if (!isAuthenticated || !hasPaidSubscription) {
    return null;
  }

  return (
    <>
      <Button
        onClick={handleChatClick}
        size="lg"
        className="fixed bottom-4 right-4 z-30 w-14 h-14 rounded-full text-white shadow-lg hover:shadow-xl transition-all duration-300 hover:scale-110"
        style={{
          background: `linear-gradient(45deg, ${colors.primary}, ${colors.secondary})`,
          boxShadow: `0 8px 32px ${colors.primary}40`,
        }}
        onMouseEnter={(e) => {
          e.currentTarget.style.transform = "scale(1.1)";
          e.currentTarget.style.boxShadow = `0 12px 40px ${colors.primary}60`;
        }}
        onMouseLeave={(e) => {
          e.currentTarget.style.transform = "scale(1)";
          e.currentTarget.style.boxShadow = `0 8px 32px ${colors.primary}40`;
        }}
        aria-label="Open live chat"
      >
        <MessageCircle className="h-6 w-6" />
      </Button>

      {/* Premium Feature Notification - Top Right Corner */}
      {showNotification && (
        <div className="fixed top-4 right-4 z-50 animate-in fade-in slide-in-from-top-2 duration-300">
          <Card
            className="w-80 shadow-xl border-2 backdrop-blur-sm"
            style={{
              backgroundColor: colors.surface,
              borderColor: colors.primary,
              boxShadow: `0 10px 40px ${colors.primary}30`,
            }}
          >
            <CardContent className="p-6 relative">
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setShowNotification(false)}
                className="absolute top-2 right-2 w-6 h-6 p-0 hover:bg-transparent"
                style={{ color: colors.textSecondary }}
              >
                <X className="h-4 w-4" />
              </Button>

              <div className="text-center space-y-4">
                <div className="flex items-center justify-center">
                  <Crown
                    className="w-8 h-8 mr-2"
                    style={{ color: colors.primary }}
                  />
                  <h3
                    className="font-bold text-lg"
                    style={{ color: colors.primary }}
                  >
                    Premium Feature
                  </h3>
                </div>

                {!isAuthenticated ? (
                  <>
                    <p className="text-sm" style={{ color: colors.text }}>
                      Live chat is available exclusively to premium subscribers.
                      Create an account and upgrade to join the conversation!
                    </p>
                    <div className="space-y-2">
                      <p
                        className="text-xs"
                        style={{ color: colors.textSecondary }}
                      >
                        ‚úì Live Chat Access ‚úì Song Submissions ‚úì Exclusive
                        Content
                      </p>
                      <div className="flex gap-2">
                        <Button
                          size="sm"
                          onClick={handleSignIn}
                          className="flex-1 border-0"
                          style={{
                            backgroundColor: colors.primary,
                            color: colors.primaryText || "white",
                          }}
                        >
                          <LogIn className="w-4 h-4 mr-1" />
                          Sign In
                        </Button>
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={handleSubscribe}
                          className="flex-1"
                          style={{
                            borderColor: colors.primary,
                            color: colors.primary,
                            backgroundColor: "transparent",
                          }}
                        >
                          Subscribe
                        </Button>
                      </div>
                    </div>
                  </>
                ) : (
                  <>
                    <p className="text-sm" style={{ color: colors.text }}>
                      Live chat requires a premium subscription. Upgrade now to
                      join the metalhead community!
                    </p>
                    <div className="space-y-2">
                      <p
                        className="text-xs"
                        style={{ color: colors.textSecondary }}
                      >
                        ‚úì Live Chat Access ‚úì Song Submissions ‚úì Exclusive
                        Content
                      </p>
                      <Button
                        size="sm"
                        onClick={handleSubscribe}
                        className="w-full text-white border-0"
                        style={{ backgroundColor: colors.primary }}
                      >
                        Upgrade Now
                      </Button>
                    </div>
                  </>
                )}
              </div>
            </CardContent>
          </Card>
        </div>
      )}
    </>
  );
}

client/src/components/Contact.tsx.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import {
  Send,
  Mail,
  Clock,
  Radio,
  Facebook,
  Twitter,
  Instagram,
  Youtube,
  Check,
  X,
  Copy,
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { useTheme } from "@/contexts/ThemeContext";
import type { InsertContact } from "@shared/schema";
import FadeInView from "./FadeInView";
import StaggeredAnimation from "./StaggeredAnimation";

export default function Contact() {
  const [formData, setFormData] = useState<InsertContact>({
    firstName: "",
    lastName: "",
    email: "",
    subject: "",
    message: "",
  });

  const [validationErrors, setValidationErrors] = useState<string[]>([]);
  const [showSuccess, setShowSuccess] = useState(false);
  const [showError, setShowError] = useState(false);
  const [originalMessage, setOriginalMessage] = useState<string>("");

  const { toast } = useToast();
  const { getColors, currentTheme } = useTheme();
  const colors = getColors();

  const contactMutation = useMutation({
    mutationFn: async (data: InsertContact) => {
      const response = await apiRequest("POST", "/api/contacts", data);
      return response.json();
    },
    onSuccess: () => {
      setOriginalMessage(formData.message);
      setShowSuccess(true);
      setFormData({
        firstName: "",
        lastName: "",
        email: "",
        subject: "",
        message: "",
      });
      setValidationErrors([]);
    },
    onError: () => {
      setOriginalMessage(formData.message);
      setShowError(true);
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // Validate form
    const errors: string[] = [];
    if (!formData.firstName.trim()) errors.push("First Name");
    if (!formData.lastName.trim()) errors.push("Last Name");
    if (!formData.email.trim()) errors.push("Email Address");
    if (!formData.subject.trim() || formData.subject === "Select a subject") errors.push("Subject");
    if (!formData.message.trim()) errors.push("Message");

    // Validate email format
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (formData.email && !emailRegex.test(formData.email)) {
      errors.push("Please enter a valid email address");
    }

    if ((errors?.length || 0) > 0) {
      setValidationErrors(errors);
      return;
    }

    setValidationErrors([]);
    contactMutation.mutate(formData);
  };

  const handleCopyMessage = () => {
    if (originalMessage) {
      navigator.clipboard.writeText(originalMessage);
      toast({
        title: "Message Copied",
        description: "Your message has been copied to clipboard.",
      });
    }
  };

  const handleTryAgain = () => {
    setShowError(false);
    setShowSuccess(false);
  };

  const handleInputChange = (field: keyof InsertContact, value: string) => {
    setFormData((prev) => ({ ...prev, [field]: value }));
  };

  return (
    <section 
      id="contact" 
      className="py-20 transition-colors duration-300"
      style={{ backgroundColor: colors.background }}
    >
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-12">
          <h2 
            className="font-orbitron font-black text-3xl md:text-4xl mb-4"
            style={{ 
              color: currentTheme === 'light-mode' ? '#000000' : colors.text 
            }}
          >
            GET IN TOUCH
          </h2>
          <p 
            className="text-lg font-semibold"
            style={{ color: colors.textMuted }}
          >
            Have questions, feedback, or want to get involved? Drop us a line!
          </p>

        </div>

        <div className="flex justify-center items-center min-h-[500px]">
          <div className="w-full max-w-lg">
            <Card
              className="transition-all duration-300 mx-auto"
              style={{ 
                backgroundColor: colors.card,
                borderColor: colors.primary
              }}
            >
              <CardContent className="p-8">
                {!showSuccess && !showError && (
                  <>
                    <h3 
                      className="font-black text-xl mb-6 text-center"
                      style={{ color: colors.primary }}
                    >
                      Send us a Message
                    </h3>

                    {(validationErrors?.length || 0) > 0 && (
                      <div className="mb-4 p-3 bg-red-900/20 border border-red-500 rounded-md">
                        <p className="text-red-400 text-sm font-semibold">
                          Please fill out the following required fields:
                        </p>
                        <ul className="text-red-300 text-sm mt-1">
                          {validationErrors.map((field, index) => (
                            <li key={index}>‚Ä¢ {field}</li>
                          ))}
                        </ul>
                      </div>
                    )}

                    <form onSubmit={handleSubmit} className="space-y-6">
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        <div>
                          <Label
                            htmlFor="firstName"
                            className="font-semibold"
                            style={{ color: colors.textSecondary }}
                          >
                            First Name{validationErrors.includes("First Name") && <span className="text-red-500">*</span>}
                          </Label>
                          <Input
                            id="firstName"
                            value={formData.firstName}
                            onChange={(e) =>
                              handleInputChange("firstName", e.target.value)
                            }
                            placeholder="John"
                            required
                            className="transition-colors duration-300 placeholder:text-gray-400 placeholder:opacity-50"
                            style={{
                              backgroundColor: colors.surface,
                              borderColor: colors.border,
                              color: colors.text
                            }}
                          />
                        </div>
                        <div>
                          <Label
                            htmlFor="lastName"
                            className="font-semibold"
                            style={{ color: colors.textSecondary }}
                          >
                            Last Name{validationErrors.includes("Last Name") && <span className="text-red-500">*</span>}
                          </Label>
                          <Input
                            id="lastName"
                            value={formData.lastName}
                            onChange={(e) =>
                              handleInputChange("lastName", e.target.value)
                            }
                            placeholder="Doe"
                            required
                            className="transition-colors duration-300 placeholder:text-gray-400 placeholder:opacity-50"
                            style={{
                              backgroundColor: colors.surface,
                              borderColor: colors.border,
                              color: colors.text
                            }}
                          />
                        </div>
                      </div>

                      <div>
                        <Label
                          htmlFor="email"
                          className="font-semibold"
                          style={{ color: colors.textSecondary }}
                        >
                          Email Address{validationErrors.includes("Email Address") && <span className="text-red-500">*</span>}
                        </Label>
                        <Input
                          id="email"
                          type="email"
                          value={formData.email}
                          onChange={(e) =>
                            handleInputChange("email", e.target.value)
                          }
                          placeholder="john@example.com"
                          required
                          className="transition-colors duration-300 placeholder:text-gray-400 placeholder:opacity-50"
                          style={{
                            backgroundColor: colors.surface,
                            borderColor: colors.border,
                            color: colors.text
                          }}
                        />
                      </div>

                      <div>
                        <Label
                          htmlFor="subject"
                          className="font-semibold"
                          style={{ color: colors.textSecondary }}
                        >
                          Subject{validationErrors.includes("Subject") && <span className="text-red-500">*</span>}
                        </Label>
                        <Select
                          value={formData.subject}
                          onValueChange={(value) =>
                            handleInputChange("subject", value)
                          }
                        >
                          <SelectTrigger 
                            className="transition-colors duration-300" 
                            style={{
                              backgroundColor: colors.surface,
                              borderColor: colors.border,
                              color: colors.text
                            }}
                            aria-label="Select message subject"
                          >
                            <SelectValue placeholder="Select a subject" />
                          </SelectTrigger>
                          <SelectContent 
                            style={{
                              backgroundColor: colors.surface,
                              borderColor: colors.border
                            }}
                          >
                            <SelectItem value="general">
                              General Inquiry
                            </SelectItem>
                            <SelectItem value="technical">
                              Technical Support
                            </SelectItem>
                            <SelectItem value="partnership">
                              Partnership/Collaboration
                            </SelectItem>
                            <SelectItem value="feedback">Feedback</SelectItem>
                            <SelectItem value="other">Other</SelectItem>
                          </SelectContent>
                        </Select>
                      </div>

                      <div>
                        <Label
                          htmlFor="contactMessage"
                          className="font-semibold"
                          style={{ color: colors.textSecondary }}
                        >
                          Message{validationErrors.includes("Message") && <span className="text-red-500">*</span>}
                        </Label>
                        <Textarea
                          id="contactMessage"
                          value={formData.message}
                          onChange={(e) =>
                            handleInputChange("message", e.target.value)
                          }
                          placeholder="Tell us what's on your mind..."
                          rows={5}
                          required
                          className="resize-none transition-colors duration-300 placeholder:text-gray-400 placeholder:opacity-50"
                          style={{
                            backgroundColor: colors.surface,
                            borderColor: colors.border,
                            color: colors.text
                          }}
                        />
                      </div>

                      <Button
                        type="submit"
                        disabled={contactMutation.isPending}
                        className="w-full px-6 py-3 rounded-full font-bold transition-all duration-300"
                        style={{
                          backgroundColor: colors.primary,
                          color: colors.primaryText || "white",
                        }}
                        onMouseEnter={(e) => {
                          e.currentTarget.style.backgroundColor =
                            colors.primaryDark || colors.primary;
                          e.currentTarget.style.transform = "scale(1.02)";
                        }}
                        onMouseLeave={(e) => {
                          e.currentTarget.style.backgroundColor =
                            colors.primary;
                          e.currentTarget.style.transform = "scale(1)";
                        }}
                      >
                        <Send className="mr-2 h-4 w-4" />
                        {contactMutation.isPending
                          ? "SENDING..."
                          : "SEND MESSAGE"}
                      </Button>
                    </form>
                  </>
                )}

                {/* Success State */}
                {showSuccess && (
                  <div className="text-center animate-in fade-in duration-500">
                    <div className="relative inline-flex items-center justify-center mb-4">
                      <div
                        className="absolute w-16 h-16 rounded-full opacity-50"
                        style={{ backgroundColor: colors.primary }}
                      ></div>
                      <Check className="w-8 h-8 text-green-400 relative z-10" />
                    </div>
                    <h3 className="font-black text-xl mb-2 text-green-400">
                      Message Sent Successfully!
                    </h3>
                    <p className="text-gray-300 text-sm">
                      We'll get back to you within 24-48 hours.
                    </p>
                    <Button
                      onClick={handleTryAgain}
                      className="mt-4 px-6 py-2 rounded-full font-bold transition-all duration-300"
                      style={{
                        backgroundColor: colors.primary,
                        color: colors.primaryText || "white",
                      }}
                    >
                      Send Another Message
                    </Button>
                  </div>
                )}

                {/* Error State */}
                {showError && (
                  <div className="text-center animate-in fade-in duration-500">
                    <div className="relative inline-flex items-center justify-center mb-4">
                      <div className="absolute w-16 h-16 rounded-full bg-red-600 opacity-50"></div>
                      <X className="w-8 h-8 text-red-400 relative z-10" />
                    </div>
                    <h3 className="font-black text-xl mb-2 text-red-400">
                      Message Failed to Send
                    </h3>
                    <p className="text-gray-300 text-sm mb-4">
                      Please refresh the site and try again.
                    </p>

                    <div className="space-y-3">
                      <Button
                        onClick={handleCopyMessage}
                        className="w-full px-6 py-2 rounded-full font-bold transition-all duration-300 bg-gray-600 hover:bg-gray-500 text-white"
                      >
                        <Copy className="mr-2 h-4 w-4" />
                        Copy Message to Clipboard
                      </Button>

                      <Button
                        onClick={handleTryAgain}
                        className="w-full px-6 py-2 rounded-full font-bold transition-all duration-300"
                        style={{
                          backgroundColor: colors.primary,
                          color: colors.primaryText || "white",
                        }}
                      >
                        Try Again
                      </Button>
                    </div>
                  </div>
                )}
              </CardContent>
            </Card>
          </div>
        </div>
      </div>
    </section>
  );
}

