  userLocation,
  setSelectedListener,
  setIsUserLocationSelected,
}: {
  listeners: ActiveListener[];
  colors: any;
  isDarkMode: boolean;
  themeName: string;
  onListenerClick: (listener: ActiveListener) => void;
  selectedListener: ActiveListener | null;
  apiKey: string;
  userLocation?: { lat: number; lng: number };
  setSelectedListener: (listener: ActiveListener | null) => void;
  setIsUserLocationSelected: (selected: boolean) => void;
}) => {
  const mapRef = useRef<HTMLDivElement>(null);
  const [isMapLoaded, setIsMapLoaded] = useState(false);
  const [mapError, setMapError] = useState<string | null>(null);

  useEffect(() => {
    const loadGoogleMaps = async () => {
      console.log('=== Google Maps Loading Debug ===');
      console.log('Map ref current:', !!mapRef.current);
      console.log('API key available:', !!apiKey);
      console.log('API key value:', apiKey?.substring(0, 15) + '...');

