import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Separator } from "@/components/ui/separator";
import { useTheme } from "@/contexts/ThemeContext";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Link } from "wouter";
import { ArrowLeft, Mail, Lock, User, Eye, EyeOff } from "lucide-react";
import { cn } from "@/lib/utils";

export default function LoginPage() {
  const [isLogin, setIsLogin] = useState(true);
  const [showPassword, setShowPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    firstName: "",
    lastName: "",
    confirmPassword: "",
  });

  const { getColors, theme } = useTheme();
  const { toast } = useToast();
  const colors = getColors();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      if (!isLogin) {
        // Registration validation
        if (formData.password !== formData.confirmPassword) {
          toast({
            title: "Password Mismatch",
            description: "Passwords do not match.",
            variant: "destructive",
          });
          return;
        }

        if (formData.password.length < 6) {
          toast({
            title: "Password Too Short",
            description: "Password must be at least 6 characters.",
            variant: "destructive",
          });
          return;
        }
      }

      const endpoint = isLogin ? "/api/auth/login" : "/api/auth/register";
      const payload = isLogin
        ? { email: formData.email, password: formData.password }
        : {
            email: formData.email,
            password: formData.password,
            firstName: formData.firstName,
            lastName: formData.lastName,
          };

      const response = await apiRequest("POST", endpoint, payload);
      const data = await response.json();

      if (isLogin) {
        await login(formData.email, formData.password);
        toast({
          title: "Welcome Back!",
          description: "You have successfully signed in.",
        });
        navigate("/");
      } else {
        await register(
          formData.email,
          formData.password,
          `${formData.firstName} ${formData.lastName}`,
          formData.firstName,
          formData.lastName,
          formData.phoneNumber || ""
        );
        toast({
          title: "Account Created!",
          description: "Your account has been created successfully. Welcome to Spandex Salvation Radio!",
        });
        navigate("/");
      }
    } catch (error: any) {
      console.error("Authentication error:", error);
      toast({
        title: isLogin ? "Login Failed" : "Registration Failed",
        description: error.message || "Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleGoogleAuth = () => {
    // Redirect to Google OAuth endpoint
    window.location.href = "/api/auth/google";
  };

  return (
    <div
      className={cn(
        "min-h-screen flex items-center justify-center p-4",
        (theme as any) === "light" ? "bg-white" : "bg-black"
      )}
    >
      <Card className="w-full max-w-md">
        <CardHeader className="space-y-4">
          <div className="flex items-center space-x-2">
            <Link href="/">
              <Button variant="ghost" size="sm">
                <ArrowLeft className="h-4 w-4 mr-2" />
                Back to Home
              </Button>
            </Link>
          </div>
          <div className="text-center">
            <CardTitle className="text-2xl font-bold">
              {isLogin ? "Welcome Back" : "Create Account"}
            </CardTitle>
            <p className="text-muted-foreground mt-2">
              {isLogin
                ? "Login to your Spandex Salvation account"
                : "Join the metal revolution"}
            </p>
          </div>
        </CardHeader>

        <CardContent className="space-y-6">
          {/* Google Auth Button */}
          <Button
            type="button"
            variant="outline"
            className="w-full bg-white text-black border-gray-300 hover:bg-gray-50"
            onClick={handleGoogleAuth}
          >
            <img
              src="/assets/icons8-google_1750360286324.png"
              alt="Google"
              className="w-4 h-4 mr-2"
            />
            {isLogin ? "Continue with Google" : "Sign Up with Google"}
          </Button>

          <div className="relative">
            <div className="absolute inset-0 flex items-center">
              <Separator className="w-full" />
            </div>
            <div className="relative flex justify-center text-xs uppercase">
              <span className="bg-background px-2 text-muted-foreground">
                Or continue with email
              </span>
            </div>
          </div>

          {/* Email/Password Form */}
          <form onSubmit={handleSubmit} className="space-y-4">
            {!isLogin && (
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="firstName">First Name</Label>
                  <div className="relative">
                    <User className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                    <Input
                      id="firstName"
                      type="text"
                      placeholder="John"
                      className="pl-10"
                      value={formData.firstName}
                      onChange={(e) =>
                        setFormData((prev) => ({
                          ...prev,
                          firstName: e.target.value,
                        }))
                      }
                      required
                    />
                  </div>
                </div>
                <div>
                  <Label htmlFor="lastName">Last Name</Label>
                  <div className="relative">
                    <User className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                    <Input
                      id="lastName"
                      type="text"
                      placeholder="Doe"
                      className="pl-10"
                      value={formData.lastName}
                      onChange={(e) =>
                        setFormData((prev) => ({
                          ...prev,
                          lastName: e.target.value,
                        }))
                      }
                      required
                    />
                  </div>
                </div>
              </div>
            )}

            <div>
              <Label htmlFor="email">Email</Label>
              <div className="relative">
                <Mail className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                <Input
                  id="email"
                  type="email"
                  placeholder="john@example.com"
                  className="pl-10"
                  value={formData.email}
                  onChange={(e) =>
                    setFormData((prev) => ({ ...prev, email: e.target.value }))
                  }
                  required
                />
              </div>
            </div>

            <div>
              <Label htmlFor="password">Password</Label>
              <div className="relative">
                <Lock className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                <Input
                  id="password"
                  type={showPassword ? "text" : "password"}
                  placeholder={
                    isLogin ? "Enter your password" : "Create a password"
                  }
                  className="pl-10 pr-10"
                  value={formData.password}
                  onChange={(e) =>
                    setFormData((prev) => ({
                      ...prev,
                      password: e.target.value,
                    }))
                  }
                  required
                />
                <button
                  type="button"
                  onClick={() => setShowPassword(!showPassword)}
                  className="absolute right-3 top-3 text-muted-foreground hover:text-foreground"
                >
                  {showPassword ? (
                    <EyeOff className="h-4 w-4" />
                  ) : (
                    <Eye className="h-4 w-4" />
                  )}
                </button>
              </div>
            </div>

            {!isLogin && (
              <div>
                <Label htmlFor="confirmPassword">Confirm Password</Label>
                <div className="relative">
                  <Lock className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                  <Input
                    id="confirmPassword"
                    type={showPassword ? "text" : "password"}
                    placeholder="Confirm your password"
                    className="pl-10"
                    value={formData.confirmPassword}
                    onChange={(e) =>
                      setFormData((prev) => ({
                        ...prev,
                        confirmPassword: e.target.value,
                      }))
                    }
                    required
                  />
                </div>
              </div>
            )}

            <Button
              type="submit"
              className="w-full"
              disabled={isLoading}
              style={{ backgroundColor: colors.primary }}
            >
              {isLoading
                ? "Please wait..."
                : isLogin
                  ? "Login"
                  : "Create Account"}
            </Button>
          </form>

          <div className="text-center">
            <button
              type="button"
              onClick={() => setIsLogin(!isLogin)}
              className="text-sm text-muted-foreground hover:text-foreground underline"
            >
              {isLogin
                ? "Don't have an account? Sign up"
                : "Already have an account? Login"}
            </button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/MusicPage.tsx.content:
import { useState } from "react";
import RadioCoPlayer from "@/components/RadioCoPlayer";
import Navigation from "@/components/Navigation";
import StickyPlayer from "@/components/StickyPlayer";
import { ChevronDown } from "lucide-react";
import { useTheme } from "@/contexts/ThemeContext";
import { useRadio } from "@/contexts/RadioContext";

type Station = "95.5 The Beat" | "Hot 97" | "Power 106" | "SomaFM Metal";

export default function MusicPage() {
  const { colors } = useTheme();
  const [currentStation, setCurrentStation] = useState<Station>("95.5 The Beat");
  const [isStationDropdownOpen, setIsStationDropdownOpen] = useState(false);

  const stations: Station[] = ["95.5 The Beat", "Hot 97", "Power 106", "SomaFM Metal"];

  const stationInfo: Record<Station, { name: string; description: string; frequency: string; listeners: string }> = {
    "95.5 The Beat": {
      name: "95.5 The Beat",
      description: "Dallas' Hip Hop and R&B - Playing the hottest tracks 24/7",
      frequency: "95.5 FM",
      listeners: "100K+"
    },
    "Hot 97": {
      name: "Hot 97",
      description: "New York's Hip Hop & R&B - Where Hip Hop Lives",
      frequency: "97.1 FM",
      listeners: "500K+"
    },
    "Power 106": {
      name: "Power 106",
      description: "Los Angeles' Hip Hop Station - LA's #1 for Hip Hop",
      frequency: "105.9 FM", 
      listeners: "300K+"
    },
    "SomaFM Metal": {
      name: "SomaFM Metal Detector",
      description: "From black to doom, prog to sludge, thrash to post, stoner to crossover",
      frequency: "Online",
      listeners: "50K+"
    }
  };

  const currentStationInfo = stationInfo[currentStation];

  return (
    <>
      <Navigation />
      <div id="music" className="min-h-screen bg-background pt-20 pb-8">
        <div className="container mx-auto px-4">
          {/* Header */}
          <div className="text-center mb-8">
            <h1 className="text-4xl font-black text-foreground mb-4">
              LIVE RADIO
            </h1>
            <p className="text-xl text-muted-foreground mb-6">
              Stream Your Favorite Stations 24/7
            </p>
          </div>

          {/* Station Selector - Centered Above Player */}
          <div className="max-w-4xl mx-auto mb-6">
            <div className="flex justify-center">
              <div className="relative">
                <button
                  onClick={() => setIsStationDropdownOpen(!isStationDropdownOpen)}
                  className="flex items-center space-x-2 px-6 py-3 rounded-lg transition-all duration-200 font-semibold text-white"
                  style={{
                    backgroundColor: colors.primary,
                    border: `2px solid ${colors.primary}`
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.backgroundColor = colors.secondary;
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.backgroundColor = colors.primary;
                  }}
                >
                  <span>{currentStation}</span>
                  <ChevronDown 
                    size={16} 
                    style={{ 
                      transform: isStationDropdownOpen ? 'rotate(180deg)' : 'rotate(0deg)',
                      transition: 'transform 0.2s ease'
                    }} 
                  />
                </button>

                {/* Station Dropdown */}
                {isStationDropdownOpen && (
                  <div 
                    className="absolute top-full left-1/2 transform -translate-x-1/2 mt-2 py-2 rounded-lg shadow-xl border backdrop-blur-md z-50"
                    style={{
                      backgroundColor: colors.background === '#000000' ? 'rgba(0, 0, 0, 0.95)' : 'rgba(255, 255, 255, 0.95)',
                      borderColor: colors.primary + '40',
                      minWidth: '200px'
                    }}
                  >
                    {stations.map((station) => (
                      <button
                        key={station}
                        onClick={() => {
                          setCurrentStation(station);
                          setIsStationDropdownOpen(false);
                        }}
                        className="w-full px-4 py-2 text-left hover:rounded transition-colors duration-200"
                        style={{
                          color: currentStation === station ? 'white' : colors.text,
                          backgroundColor: currentStation === station ? colors.primary : 'transparent'
                        }}
                        onMouseEnter={(e) => {
                          if (currentStation !== station) {
                            e.currentTarget.style.backgroundColor = colors.primary + '20';
                          }
                        }}
                        onMouseLeave={(e) => {
                          if (currentStation !== station) {
                            e.currentTarget.style.backgroundColor = 'transparent';
                          }
                        }}
                      >
                        {station}
                      </button>
                    ))}
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Radio Player */}
          <div className="max-w-4xl mx-auto">
            <RadioCoPlayer />
          </div>

          {/* Station Info */}
          <div className="max-w-4xl mx-auto mt-8">
            <div className="bg-black/90 backdrop-blur-md rounded-lg p-6 border" style={{ borderColor: colors.primary + '20' }}>
              <h2 className="text-2xl font-bold mb-4" style={{ color: colors.text }}>
                About {currentStationInfo.name}
              </h2>
              <p className="mb-4" style={{ color: colors.text, opacity: 0.8 }}>
                {currentStationInfo.description}
              </p>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mt-6">
                <div className="text-center">
                  <div className="font-bold text-xl" style={{ color: colors.primary }}>24/7</div>
                  <div style={{ color: colors.text, opacity: 0.6 }}>Live Broadcasting</div>
                </div>
                <div className="text-center">
                  <div className="font-bold text-xl" style={{ color: colors.primary }}>
                    {currentStationInfo.frequency}
                  </div>
                  <div style={{ color: colors.text, opacity: 0.6 }}>Frequency</div>
                </div>
                <div className="text-center">
                  <div className="font-bold text-xl" style={{ color: colors.primary }}>
                    {currentStationInfo.listeners}
                  </div>
                  <div style={{ color: colors.text, opacity: 0.6 }}>Active Listeners</div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <StickyPlayer />
    </>
  );
}

