import { useState, useEffect, useCallback } from "react";
import { ChevronLeft, ChevronRight, Check, Star, Zap, Crown } from "lucide-react";
import { Button } from "@/components/ui/button";
import { useTheme } from "@/contexts/ThemeContext";
import { cn } from "@/lib/utils";

// Import package icons
import RebelPackageIcon from "@assets/RebelPackageIcon.png";
import LegendPackageIcon from "@assets/LegendPackageIcon.png";
import IconPackageIcon from "@assets/IconPackageIcon.png";

interface SubscriptionTier {
  id: string;
  name: string;
  price: number;
  priceId: string;
  icon: string;
  iconElement: React.ReactNode;
  gradientStart: string;
  gradientEnd: string;
  features: string[];
  popular?: boolean;
  description: string;
  perks: string[];
}

const subscriptionTiers: SubscriptionTier[] = [
  {
    id: "rebel",
    name: "REBEL",
    price: 5.99,
    priceId: "price_rebel_monthly",
    icon: RebelPackageIcon,
    iconElement: <Zap className="w-16 h-16" />,
    gradientStart: "#B56BFF",
    gradientEnd: "#FF50C3",
    description: "Start your metal journey",
    features: [
      "Ad-free streaming experience",
      "High-quality audio (320kbps)",
      "Monthly exclusive playlist",
      "Priority song requests",
    ],
    perks: ["üé∏ Guitar picks monthly", "‚ö° Early access to new shows"],
  },
  {
    id: "legend",
    name: "LEGEND",
    price: 12.99,
    priceId: "price_legend_monthly",
    popular: true,
    icon: LegendPackageIcon,
    iconElement: <Star className="w-16 h-16" />,
    gradientStart: "#E520C6",
    gradientEnd: "#F4654F",
    description: "Become a true metal legend",
    features: [
      "Everything in Rebel tier",
      "Exclusive live show access",
      "Artist interview archives",
      "VIP Discord community",
      "Monthly exclusive merch discount",
    ],
    perks: ["üé§ Monthly video calls with DJs", "üéÅ Quarterly mystery box", "üíø Signed albums"],
  },
  {
    id: "icon",
    name: "ICON",
    price: 24.99,
    priceId: "price_icon_monthly",
    icon: IconPackageIcon,
    iconElement: <Crown className="w-16 h-16" />,
    gradientStart: "#FF50C3",
    gradientEnd: "#B66BFF",
    description: "The ultimate metal experience",
    features: [
      "Everything in Legend tier",
      "Personal DJ requests",
      "Exclusive artist meet & greets",
      "Limited edition vinyl access",
      "Co-host opportunities",
    ],
    perks: ["üé§ Name a weekly show segment", "üèÜ Annual VIP concert tickets", "üëï Custom merch line"],
  },
];

export default function SubscriptionCarousel() {
  const [currentIndex, setCurrentIndex] = useState(1); // Start with Legend (most popular)
  const [isAnimating, setIsAnimating] = useState(false);
  const [slideDirection, setSlideDirection] = useState<'left' | 'right'>('right');
  const [hoveredTier, setHoveredTier] = useState<string | null>(null);
  const { getColors, currentTheme } = useTheme();
  const colors = getColors();

  const handlePrevious = useCallback(() => {
    if (!isAnimating) {
      console.log('Previous animation starting');
      setIsAnimating(true);
      setSlideDirection('right');
      setCurrentIndex((prev) => (prev - 1 + (subscriptionTiers?.length || 0)) % (subscriptionTiers?.length || 1));
      setTimeout(() => {
        setIsAnimating(false);
        console.log('Previous animation complete');
      }, 400);
    }
  }, [isAnimating]);

  const handleNext = useCallback(() => {
    if (!isAnimating) {
      console.log('Next animation starting');
      setIsAnimating(true);
      setSlideDirection('left');
      setCurrentIndex((prev) => (prev + 1) % (subscriptionTiers?.length || 1));
      setTimeout(() => {
        setIsAnimating(false);
        console.log('Next animation complete');
      }, 400);
    }
  }, [isAnimating]);

  // Keyboard navigation
  useEffect(() => {
    const handleKeyDown = (event: KeyboardEvent) => {
      if (event.key === 'ArrowLeft') {
        event.preventDefault();
        handlePrevious();
      } else if (event.key === 'ArrowRight') {
        event.preventDefault();
        handleNext();
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [handlePrevious, handleNext]);

  const handleSelectTier = (index: number) => {
    if (!isAnimating && index !== currentIndex) {
      setIsAnimating(true);
      setCurrentIndex(index);
      setTimeout(() => setIsAnimating(false), 500);
    }
  };

  const currentTier = subscriptionTiers[currentIndex];

  return (
    <div className="relative w-full max-w-6xl mx-auto px-4">
      {/* 3D Carousel Container - Responsive height with device compatibility */}
      <div className="relative perspective-1000 subscription-carousel-container overflow-visible py-2 sm:py-4 md:py-6 lg:py-8"
           style={{
             minHeight: '700px',
             height: 'auto'
           }}>
        {/* Navigation Buttons */}
        <button
          onClick={handlePrevious}
          className="absolute left-2 top-1/2 transform -translate-y-1/2 z-10 bg-black/50 hover:bg-black/70 rounded-full p-2 transition-colors"
          disabled={isAnimating}
          title="Previous subscription"
        >
          <ChevronLeft className="w-8 h-8 text-white hover:scale-110 transition-transform drop-shadow-md" />
        </button>

        <button
          onClick={handleNext}
          className="absolute right-2 top-1/2 transform -translate-y-1/2 z-10 bg-black/50 hover:bg-black/70 rounded-full p-2 transition-colors"
          disabled={isAnimating}
          title="Next subscription"
        >
          <ChevronRight className="w-8 h-8 text-white hover:scale-110 transition-transform drop-shadow-md" />
        </button>

        {/* Main Card Display */}
        <div className="flex items-center justify-center h-full relative">
          <div
            className="relative w-full max-w-md"
            style={{
              transform: isAnimating ? 
                (slideDirection === 'left' ? 
                  'translateX(30px) scale(0.98)' : 
                  'translateX(-30px) scale(0.98)'
                ) : 'translateX(0) scale(1)',
              opacity: isAnimating ? 0.8 : 1,
              transition: 'all 0.4s ease-in-out'
            }}
          >
            {/* Glow Effect - properly contained */}
            <div className="absolute inset-4 rounded-3xl overflow-hidden">
              <div
                className="absolute inset-0 blur-2xl opacity-40"
                style={{
                  background: `linear-gradient(135deg, ${currentTier.gradientStart}, ${currentTier.gradientEnd})`,
                }}
              />
            </div>

            {/* Main Card */}
            <div
              className="relative backdrop-blur-xl rounded-3xl p-6 transform transition-all duration-500 hover:scale-105 overflow-hidden flex flex-col subscription-card"
              style={{
                border: currentTier.popular ? "8px solid transparent" : "5px solid transparent",
                borderRadius: "24px",
                backgroundColor: currentTheme === 'light-mode' ? '#ffffff' : 'rgba(0, 0, 0, 0.8)',
                background: currentTier.popular 
                  ? `linear-gradient(${currentTheme === 'light-mode' ? '#ffffff' : 'var(--background)'}, ${currentTheme === 'light-mode' ? '#ffffff' : 'var(--background)'}) padding-box, linear-gradient(90deg, ${currentTier.gradientStart} 0%, ${currentTier.gradientEnd} 25%, ${currentTier.gradientStart} 50%, ${currentTier.gradientEnd} 75%, ${currentTier.gradientStart} 100%) border-box`
                  : `linear-gradient(${currentTheme === 'light-mode' ? '#ffffff' : 'var(--background)'}, ${currentTheme === 'light-mode' ? '#ffffff' : 'var(--background)'}) padding-box, linear-gradient(90deg, ${currentTier.gradientStart}, ${currentTier.gradientEnd}) border-box`,
                backgroundSize: currentTier.popular ? "300% 300%" : "100% 100%",
                animation: currentTier.popular ? "gradientFlow 6s ease-in-out infinite" : "none",
                boxShadow: currentTier.popular 
                  ? `0 0 60px ${currentTier.gradientStart}60, 0 0 120px ${currentTier.gradientEnd}40, 0 0 160px ${currentTier.gradientStart}20`
                  : `0 20px 40px ${currentTier.gradientStart}40`,
                minHeight: "600px", // Minimum height for mobile devices
                height: "auto" // Auto height to prevent content cutoff
              }}
              onMouseEnter={() => setHoveredTier(currentTier.id)}
              onMouseLeave={() => setHoveredTier(null)}
            >


              {/* Package Icon with Animation */}
              <div className="flex justify-center mb-2 sm:mb-4">
                <div
                  className="relative p-2 sm:p-3 rounded-full"
                  style={{
                    background: `linear-gradient(135deg, ${currentTier.gradientStart}20, ${currentTier.gradientEnd}20)`,
                  }}
                >
                  <img
                    src={currentTier.icon}
                    alt={`${currentTier.name} icon`}
                    className={`w-16 h-16 sm:w-20 sm:h-20 object-contain animate-float ${
                      currentIndex === 1 ? 'float-delay-1' : 
                      currentIndex === 2 ? 'float-delay-2' : ''
                    }`}
                    style={{
                      animation: 'float 4s ease-in-out infinite',
                      animationDelay: currentIndex === 1 ? '0.5s' : currentIndex === 2 ? '1s' : '0s'
                    }}
                  />
                </div>
              </div>

              {/* Title and Description */}
              <div className="text-center mb-2 sm:mb-4">
                <h3 className="text-2xl sm:text-3xl font-black mb-1 sm:mb-2">
                  <span
                    key={currentTier.id}
                    style={{
                      background: `linear-gradient(90deg, ${currentTier.gradientStart}, ${currentTier.gradientEnd})`,
                      WebkitBackgroundClip: "text",
                      WebkitTextFillColor: "transparent",
                      backgroundClip: "text",
                      color: "transparent",
                      display: "inline",
                      padding: 0,
                      margin: 0,
                      fontWeight: "inherit",
                    }}
                  >
                    {currentTier.name}
                  </span>
                </h3>
                <p 
                  className="text-sm sm:text-base"
                  style={{ 
                    color: currentTheme === 'light-mode' ? 'rgba(0, 0, 0, 0.6)' : '#9ca3af' 
                  }}
                >
                  {currentTier.description}
                </p>

                {/* MOST POPULAR badge for Legend package */}
                {currentTier.popular && (
                  <div className="mt-4">
                    <span
                      className="px-4 py-1 rounded-full text-xs font-bold"
                      style={{
                        background: "linear-gradient(135deg, #ff6b35, #f7931e)",
                        color: "black",
                        whiteSpace: "nowrap",
                      }}
                    >
                      MOST&nbsp;POPULAR
                    </span>
                  </div>
                )}
              </div>

              {/* Price */}
              <div className="text-center mb-3 sm:mb-6">
                <div className="flex items-center justify-center">
                  <span 
                    className="text-3xl sm:text-4xl font-black"
                    style={{ 
                      color: currentTheme === 'light-mode' ? '#000000' : '#ffffff' 
                    }}
                  >
                    ${currentTier.price}
                  </span>
                  <span 
                    className="ml-2 text-sm sm:text-base"
                    style={{ 
                      color: currentTheme === 'light-mode' ? 'rgba(0, 0, 0, 0.6)' : '#9ca3af' 
                    }}
                  >
                    /month
                  </span>
                </div>
              </div>

              {/* Features - centered between price and exclusive perks */}
              <div className="flex-1 flex items-center justify-center py-2 sm:py-4">
                <div className="w-full max-w-xs">
                  {(currentTier.features || []).sort((a, b) => (a?.length || 0) - (b?.length || 0)).map((feature, index) => (
                    <div
                      key={index}
                      className="flex items-center justify-center mb-2 sm:mb-3 transform transition-all duration-300"
                      style={{
                        transform: hoveredTier === currentTier.id ? "translateX(10px)" : "translateX(0)",
                        transitionDelay: `${index * 50}ms`,
                      }}
                    >
                      <div className="flex items-center">
                        <div
                          className="w-3 h-3 sm:w-4 sm:h-4 rounded-full flex items-center justify-center flex-shrink-0 mr-2 sm:mr-3"
                          style={{
                            background: `linear-gradient(135deg, ${currentTier.gradientStart}, ${currentTier.gradientEnd})`,
                          }}
                        >
                          <Check className="w-2 h-2 sm:w-2.5 sm:h-2.5 text-white" />
                        </div>
                        <span 
                          className="text-xs sm:text-sm font-medium text-center"
                          style={{ 
                            color: currentTheme === 'light-mode' ? 'rgba(0, 0, 0, 0.7)' : '#d1d5db' 
                          }}
                        >
                          {feature}
                        </span>
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              {/* Special Perks - centered */}
              <div className="mb-2 sm:mb-3">
                <div className="bg-white/5 rounded-lg p-2 sm:p-3 max-w-xs mx-auto">
                  <h4 
                    className="text-xs font-bold mb-1 sm:mb-2 text-center"
                    style={{ 
                      color: currentTheme === 'light-mode' ? 'rgba(0, 0, 0, 0.6)' : '#9ca3af' 
                    }}
                  >
                    EXCLUSIVE PERKS
                  </h4>
                  <div className="space-y-0.5 sm:space-y-1">
                    {(currentTier.perks || []).map((perk, index) => (
                      <div 
                        key={index} 
                        className="text-xs text-center leading-tight"
                        style={{ 
                          color: currentTheme === 'light-mode' ? 'rgba(0, 0, 0, 0.7)' : '#d1d5db' 
                        }}
                      >
                        {perk}
                      </div>
                    ))}
                  </div>
                </div>
              </div>

              {/* CTA Button */}
              <div className="mt-auto mb-3 sm:mb-4">
                <Button
                  className="w-full py-3 sm:py-4 text-sm sm:text-base font-bold rounded-xl transform transition-all duration-300 hover:scale-105"
                  style={{
                    background: `linear-gradient(135deg, ${currentTier.gradientStart}, ${currentTier.gradientEnd})`,
                    boxShadow: `0 10px 30px ${currentTier.gradientStart}50`,
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.boxShadow = `0 15px 40px ${currentTier.gradientStart}70`;
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.boxShadow = `0 10px 30px ${currentTier.gradientStart}50`;
                  }}
                >
                  Get {currentTier.name.charAt(0) + currentTier.name.slice(1).toLowerCase()} Now
                </Button>
              </div>
            </div>
          </div>
        </div>

      </div>

      {/* Mobile Swipe Hint / Desktop Click Hint */}
      <div className="text-center text-gray-500 text-sm mt-8">
        <span className="md:hidden">‚Üê Swipe to explore plans ‚Üí</span>
        <span className="hidden md:inline">‚Üê Click to explore plans ‚Üí</span>
      </div>

      {/* Tier Indicators with Package Icons */}
      <div className="flex justify-center items-center space-x-6 mt-6">
        {(subscriptionTiers || []).map((tier, index) => (
          <button
            key={tier.id}
            onClick={() => handleSelectTier(index)}
            className={cn(
              "relative transition-all duration-500 ease-in-out p-3 rounded-full",
              index === currentIndex
                ? "scale-110"
                : "opacity-40 hover:opacity-60 scale-90"
            )}
          >
            {/* Smooth background glow */}
            <div
              className={cn(
                "absolute inset-0 rounded-full transition-all duration-500",
                index === currentIndex ? "opacity-100" : "opacity-0"
              )}
              style={{
                background: `radial-gradient(circle, ${tier.gradientStart}20, transparent 70%)`,
                filter: "blur(12px)",
              }}
            />

            {/* Icon container */}
            <div
              className={cn(
                "relative rounded-full transition-all duration-500",
                index === currentIndex ? "bg-black/20" : "bg-transparent"
              )}
              style={{
                padding: "0.5rem",
              }}
            >
              <img
                src={tier.icon}
                alt={`${tier.name} icon`}
                className={cn(
                  "object-contain transition-all duration-500",
                  index === currentIndex ? "w-8 h-8" : "w-6 h-6"
                )}
                style={{
                  filter: index === currentIndex 
                    ? "drop-shadow(0 0 8px rgba(255,255,255,0.5))" 
                    : "grayscale(100%) opacity(0.7)",
                }}
              />
            </div>

            {/* Active indicator ring */}
            {index === currentIndex && (
              <div
                className="absolute inset-0 rounded-full"
                style={{
                  background: `linear-gradient(135deg, ${tier.gradientStart}60, ${tier.gradientEnd}60)`,
                  padding: "2px",
                  WebkitMask: "linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0)",
                  WebkitMaskComposite: "xor",
                  maskComposite: "exclude",
                }}
              />
            )}
          </button>
        ))}
      </div>


      {/* Add gradient animation and text styles */}
      <style>{`
        @keyframes gradientFlow {
          0% { background-position: 0% 50%; }
          50% { background-position: 100% 50%; }
          100% { background-position: 0% 50%; }
        }
        .preserve-3d {
          transform-style: preserve-3d;
        }
      `}</style>
    </div>
  );
}

