import { useEffect, useState } from "react";
import { useLocation } from "wouter";
import { CheckCircle, Package, Mail, CreditCard, ArrowLeft, Download } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { useTheme } from "@/contexts/ThemeContext";

interface OrderItem {
  id: string;
  title: string;
  variant: string;
  quantity: number;
  price: number;
  image: string;
}

interface OrderDetails {
  orderId: string;
  items: OrderItem[];
  subtotal: number;
  shipping: number;
  tax: number;
  total: number;
  customerEmail: string;
  shippingAddress: {
    name: string;
    address1: string;
    address2?: string;
    city: string;
    state: string;
    zip: string;
    country: string;
  };
  paymentMethod: string;
  estimatedDelivery: string;
  trackingNumber?: string;
}

export default function OrderConfirmation() {
  const [location, setLocation] = useLocation();
  const [orderDetails, setOrderDetails] = useState<OrderDetails | null>(null);
  const [loading, setLoading] = useState(true);
  const { getColors } = useTheme();
  const colors = getColors();

  useEffect(() => {
    // Extract order details from URL parameters or localStorage
    const urlParams = new URLSearchParams(window.location.search);
    const orderId = urlParams.get('order_id');
    const productId = urlParams.get('product_id');

    if (orderId) {
      // In a real implementation, this would fetch order details from Shopify API
      // For now, we'll simulate with sample data
      setTimeout(() => {
        setOrderDetails({
          orderId: orderId,
          items: [
            {
              id: productId || "1",
              title: "Spandex Salvation Classic Tee",
              variant: "Large",
              quantity: 1,
              price: 24.99,
              image: "https://images.unsplash.com/photo-1521572163474-6864f9cf17ab?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&h=800"
            }
          ],
          subtotal: 24.99,
          shipping: 5.99,
          tax: 2.05,
          total: 33.03,
          customerEmail: "customer@example.com",
          shippingAddress: {
            name: "John Doe",
            address1: "123 Metal Street",
            city: "Rock City",
            state: "CA",
            zip: "90210",
            country: "United States"
          },
          paymentMethod: "‚Ä¢‚Ä¢‚Ä¢‚Ä¢ 4242",
          estimatedDelivery: "3-5 business days",
          trackingNumber: "1Z999AA1234567890"
        });
        setLoading(false);
      }, 1000);
    } else {
      setLoading(false);
    }
  }, []);

  if (loading) {
    return (
      <div className="min-h-screen bg-dark-bg flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 mx-auto mb-4" style={{ borderColor: colors.primary }}></div>
          <p className="text-white font-semibold">Loading order details...</p>
        </div>
      </div>
    );
  }

  if (!orderDetails) {
    return (
      <div className="min-h-screen bg-dark-bg flex items-center justify-center">
        <Card className="bg-gray-900 border-gray-800 max-w-md w-full mx-4">
          <CardContent className="p-8 text-center">
            <Package className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <h2 className="text-xl font-bold text-white mb-2">Order Not Found</h2>
            <p className="text-gray-400 mb-6">
              We couldn't find the order you're looking for.
            </p>
            <Button 
              onClick={() => setLocation('/')}
              className="w-full font-bold transition-all duration-300"
              style={{ backgroundColor: colors.primary }}
            >
              Return to Home
            </Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  const handleContinueShopping = () => {
    setLocation('/');
  };

  const handleViewOrder = () => {
    // In a real implementation, this would redirect to Shopify order tracking
    window.open(`https://spandexsalvation.myshopify.com/account/orders/${orderDetails.orderId}`, '_blank');
  };

  return (
    <div className="min-h-screen bg-dark-bg py-12 px-4">
      <div className="max-w-4xl mx-auto">
        {/* Success Header */}
        <div className="text-center mb-8">
          <div className="flex justify-center mb-4">
            <CheckCircle className="h-16 w-16" style={{ color: colors.primary }} />
          </div>
          <h1 className="text-3xl font-black text-white mb-2">Order Confirmed!</h1>
          <p className="text-gray-400 text-lg">
            Thank you for your order. We've sent a confirmation email to{" "}
            <span className="text-white font-semibold">{orderDetails.customerEmail}</span>
          </p>
        </div>

        {/* Order Details */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Order Summary */}
          <Card className="bg-gray-900 border-gray-800">
            <CardHeader>
              <CardTitle className="text-white font-black">Order Summary</CardTitle>
              <p className="text-gray-400">Order #{orderDetails.orderId}</p>
            </CardHeader>
            <CardContent className="space-y-4">
              {orderDetails.items.map((item) => (
                <div key={item.id} className="flex items-center space-x-4 p-4 bg-gray-800 rounded-lg">
                  <img 
                    src={item.image} 
                    alt={item.title}
                    className="h-16 w-16 object-cover rounded-lg"
                  />
                  <div className="flex-1">
                    <h3 className="font-bold text-white">{item.title}</h3>
                    <p className="text-gray-400 text-sm">{item.variant}</p>
                    <div className="flex justify-between items-center mt-2">
                      <span className="text-gray-400">Qty: {item.quantity}</span>
                      <span className="font-bold text-white">${item.price.toFixed(2)}</span>
                    </div>
                  </div>
                </div>
              ))}

              <div className="border-t border-gray-700 pt-4 space-y-2">
                <div className="flex justify-between text-gray-400">
                  <span>Subtotal</span>
                  <span>${orderDetails.subtotal.toFixed(2)}</span>
                </div>
                <div className="flex justify-between text-gray-400">
                  <span>Shipping</span>
                  <span>${orderDetails.shipping.toFixed(2)}</span>
                </div>
                <div className="flex justify-between text-gray-400">
                  <span>Tax</span>
                  <span>${orderDetails.tax.toFixed(2)}</span>
                </div>
                <div className="flex justify-between text-white font-bold text-lg pt-2 border-t border-gray-700">
                  <span>Total</span>
                  <span>${orderDetails.total.toFixed(2)}</span>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Shipping & Payment Info */}
          <div className="space-y-6">
            {/* Shipping Address */}
            <Card className="bg-gray-900 border-gray-800">
              <CardHeader>
                <CardTitle className="text-white font-black flex items-center">
                  <Package className="h-5 w-5 mr-2" />
                  Shipping Address
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="text-gray-300 space-y-1">
                  <p className="font-semibold text-white">{orderDetails.shippingAddress.name}</p>
                  <p>{orderDetails.shippingAddress.address1}</p>
                  {orderDetails.shippingAddress.address2 && (
                    <p>{orderDetails.shippingAddress.address2}</p>
                  )}
                  <p>
                    {orderDetails.shippingAddress.city}, {orderDetails.shippingAddress.state} {orderDetails.shippingAddress.zip}
                  </p>
                  <p>{orderDetails.shippingAddress.country}</p>
                </div>
                <div className="mt-4 p-3 bg-gray-800 rounded-lg">
                  <div className="flex items-center text-sm">
                    <Mail className="h-4 w-4 mr-2" style={{ color: colors.primary }} />
                    <span className="text-gray-400">Estimated delivery: </span>
                    <span className="text-white font-semibold ml-1">{orderDetails.estimatedDelivery}</span>
                  </div>
                  {orderDetails.trackingNumber && (
                    <div className="flex items-center text-sm mt-2">
                      <Package className="h-4 w-4 mr-2" style={{ color: colors.primary }} />
                      <span className="text-gray-400">Tracking: </span>
                      <span className="text-white font-semibold ml-1">{orderDetails.trackingNumber}</span>
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>

            {/* Payment Method */}
            <Card className="bg-gray-900 border-gray-800">
              <CardHeader>
                <CardTitle className="text-white font-black flex items-center">
                  <CreditCard className="h-5 w-5 mr-2" />
                  Payment Method
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="flex items-center justify-between">
                  <div className="flex items-center">
                    <CreditCard className="h-8 w-8 text-gray-400 mr-3" />
                    <div>
                      <p className="text-white font-semibold">Card ending in {orderDetails.paymentMethod}</p>
                      <p className="text-gray-400 text-sm">Payment processed successfully</p>
                    </div>
                  </div>
                  <Badge variant="secondary" className="bg-green-600 text-white">
                    Paid
                  </Badge>
                </div>
              </CardContent>
            </Card>

            {/* Action Buttons */}
            <div className="space-y-3">
              <Button 
                onClick={handleViewOrder}
                className="w-full font-bold transition-all duration-300"
                style={{ backgroundColor: colors.primary }}
              >
                <Package className="h-4 w-4 mr-2" />
                View Order in Account
              </Button>
              <Button 
                onClick={handleContinueShopping}
                variant="outline"
                className="w-full font-bold transition-all duration-300 border-gray-600 text-gray-300 hover:bg-gray-800"
              >
                <ArrowLeft className="h-4 w-4 mr-2" />
                Continue Shopping
              </Button>
            </div>
          </div>
        </div>

        {/* Additional Information */}
        <Card className="bg-gray-900 border-gray-800 mt-8">
          <CardContent className="p-6">
            <h3 className="text-white font-bold mb-3">What's Next?</h3>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
              <div className="flex items-start space-x-3">
                <Mail className="h-5 w-5 mt-0.5" style={{ color: colors.primary }} />
                <div>
                  <p className="text-white font-semibold">Order Confirmation</p>
                  <p className="text-gray-400">You'll receive an email confirmation shortly</p>
                </div>
              </div>
              <div className="flex items-start space-x-3">
                <Package className="h-5 w-5 mt-0.5" style={{ color: colors.primary }} />
                <div>
                  <p className="text-white font-semibold">Processing</p>
                  <p className="text-gray-400">Your order will be processed within 1-2 business days</p>
                </div>
              </div>
              <div className="flex items-start space-x-3">
                <Download className="h-5 w-5 mt-0.5" style={{ color: colors.primary }} />
                <div>
                  <p className="text-white font-semibold">Tracking</p>
                  <p className="text-gray-400">You'll receive tracking information once shipped</p>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/AdminPanel.tsx.content:
import React, { useState, useEffect } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Switch } from '@/components/ui/switch';
import { Badge } from '@/components/ui/badge';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';
import { Plus, Edit, Trash2, TestTube, Radio, AlertCircle, CheckCircle } from 'lucide-react';
import type { RadioStation, InsertRadioStation } from '@shared/schema';

interface StationFormData extends Omit<InsertRadioStation, 'apiType'> {
  apiType: 'triton' | 'streamtheworld' | 'somafm' | 'custom' | 'auto';
}

const AdminPanel: React.FC = () => {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [isCreating, setIsCreating] = useState(false);
  const [editingStation, setEditingStation] = useState<RadioStation | null>(null);
  const [formData, setFormData] = useState<StationFormData>({
    name: '',
    description: '',
    streamUrl: '',
    apiUrl: '',
    apiType: 'auto',
    stationId: '',
    frequency: '',
    location: '',
    genre: '',
    website: '',
    logo: '',
    isActive: true,
    sortOrder: 0,
  });

  // Fetch all radio stations
  const { data: stations, isLoading } = useQuery<RadioStation[]>({
    queryKey: ['/api/admin/radio-stations'],
    retry: false,
  });

  // Create station mutation
  const createMutation = useMutation({
    mutationFn: async (data: StationFormData) => {
      return await apiRequest('/api/admin/radio-stations', {
        method: 'POST',
        body: JSON.stringify(data),
      } as any);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/radio-stations'] });
      toast({
        title: "Station Created",
        description: "Radio station has been created successfully",
        variant: "default",
      });
      resetForm();
    },
    onError: (error: any) => {
      toast({
        title: "Creation Failed",
        description: error.message || "Failed to create radio station",
        variant: "destructive",
      });
    },
  });

  // Update station mutation
  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: number; data: Partial<StationFormData> }) => {
      return await apiRequest(`/api/admin/radio-stations/${id}`, {
        method: 'PUT',
        body: JSON.stringify(data),
      } as any);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/radio-stations'] });
      toast({
        title: "Station Updated",
        description: "Radio station has been updated successfully",
        variant: "default",
      });
      resetForm();
    },
    onError: (error: any) => {
      toast({
        title: "Update Failed",
        description: error.message || "Failed to update radio station",
        variant: "destructive",
      });
    },
  });

  // Delete station mutation
  const deleteMutation = useMutation({
    mutationFn: async (id: number) => {
      return await apiRequest(`/api/admin/radio-stations/${id}`, {
        method: 'DELETE',
      } as any);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/radio-stations'] });
      toast({
        title: "Station Deleted",
        description: "Radio station has been deleted successfully",
        variant: "default",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Deletion Failed",
        description: error.message || "Failed to delete radio station",
        variant: "destructive",
      });
    },
  });

  // Test station mutation
  const testMutation = useMutation({
    mutationFn: async (id: number) => {
      return await apiRequest(`/api/admin/radio-stations/${id}/test`, {
        method: 'POST',
      } as any);
    },
    onSuccess: (data) => {
      toast({
        title: "Test Complete",
        description: (data as any).message || "Station test completed",
        variant: (data as any).status === 'success' ? "default" : "destructive",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Test Failed",
        description: error.message || "Failed to test station",
        variant: "destructive",
      });
    },
  });

  // Initialize default stations mutation
  const initializeMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest('/api/admin/radio-stations/initialize', {
        method: 'POST',
      } as any);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/radio-stations'] });
      toast({
        title: "Stations Initialized",
        description: "Default radio stations have been created",
        variant: "default",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Initialization Failed",
        description: error.message || "Failed to initialize stations",
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setFormData({
      name: '',
      description: '',
      streamUrl: '',
      apiUrl: '',
      apiType: 'auto',
      stationId: '',
      frequency: '',
      location: '',
      genre: '',
      website: '',
      logo: '',
      isActive: true,
      sortOrder: 0,
    });
    setIsCreating(false);
    setEditingStation(null);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (editingStation) {
      updateMutation.mutate({ id: editingStation.id, data: formData });
    } else {
      createMutation.mutate(formData);
    }
  };

  const handleEdit = (station: RadioStation) => {
    setEditingStation(station);
    setFormData({
      name: station.name,
      description: station.description || '',
      streamUrl: station.streamUrl,
      apiUrl: station.apiUrl || '',
      apiType: station.apiType as any,
      stationId: station.stationId,
      frequency: station.frequency || '',
      location: station.location || '',
      genre: station.genre || '',
      website: station.website || '',
      logo: station.logo || '',
      isActive: station.isActive,
      sortOrder: station.sortOrder,
    });
    setIsCreating(true);
  };

  const handleDelete = (id: number) => {
    if (window.confirm('Are you sure you want to delete this radio station?')) {
      deleteMutation.mutate(id);
    }
  };

  const handleTest = (id: number) => {
    testMutation.mutate(id);
  };

  if (isLoading) {
    return (
      <div className="container mx-auto p-6">
        <div className="flex items-center justify-center h-64">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-6 max-w-6xl">
      <div className="flex items-center justify-between mb-6">
        <h1 className="text-3xl font-bold flex items-center gap-2">
          <Radio className="h-8 w-8" />
          Radio Station Admin
        </h1>
        <div className="flex gap-2">
          <Button
            onClick={() => setIsCreating(true)}
            className="flex items-center gap-2"
          >
            <Plus className="h-4 w-4" />
            Add Station
          </Button>
          <Button
            onClick={() => initializeMutation.mutate()}
            variant="outline"
            disabled={initializeMutation.isPending}
          >
            Initialize Defaults
          </Button>
        </div>
      </div>

      {/* Station Form */}
      {isCreating && (
        <Card className="mb-6">
          <CardHeader>
            <CardTitle>
              {editingStation ? 'Edit Radio Station' : 'Add New Radio Station'}
            </CardTitle>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="name">Station Name *</Label>
                  <Input
                    id="name"
                    value={formData.name}
                    onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                    required
                  />
                </div>
                <div>
                  <Label htmlFor="stationId">Station ID *</Label>
                  <Input
                    id="stationId"
                    value={formData.stationId}
                    onChange={(e) => setFormData({ ...formData, stationId: e.target.value })}
                    placeholder="e.g., hot-97"
                    required
                  />
                </div>
                <div>
                  <Label htmlFor="frequency">Frequency</Label>
                  <Input
                    id="frequency"
                    value={formData.frequency}
                    onChange={(e) => setFormData({ ...formData, frequency: e.target.value })}
                    placeholder="e.g., 97.1 FM"
                  />
                </div>
                <div>
                  <Label htmlFor="location">Location</Label>
                  <Input
                    id="location"
                    value={formData.location}
                    onChange={(e) => setFormData({ ...formData, location: e.target.value })}
                    placeholder="e.g., New York"
                  />
                </div>
                <div>
                  <Label htmlFor="genre">Genre</Label>
                  <Input
                    id="genre"
                    value={formData.genre}
                    onChange={(e) => setFormData({ ...formData, genre: e.target.value })}
                    placeholder="e.g., Hip Hop"
                  />
                </div>
                <div>
                  <Label htmlFor="apiType">API Type</Label>
                  <Select value={formData.apiType} onValueChange={(value: any) => setFormData({ ...formData, apiType: value })}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="auto">Auto Detect</SelectItem>
                      <SelectItem value="triton">Triton Digital</SelectItem>
                      <SelectItem value="streamtheworld">StreamTheWorld</SelectItem>
                      <SelectItem value="somafm">SomaFM</SelectItem>
                      <SelectItem value="custom">Custom</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <div>
                <Label htmlFor="description">Description</Label>
                <Textarea
                  id="description"
                  value={formData.description}
                  onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                  placeholder="Brief description of the station"
                />
              </div>

              <div>
                <Label htmlFor="streamUrl">Stream URL *</Label>
                <Input
                  id="streamUrl"
                  value={formData.streamUrl}
                  onChange={(e) => setFormData({ ...formData, streamUrl: e.target.value })}
                  placeholder="https://stream.example.com/radio"
                  required
                />
              </div>

              <div>
                <Label htmlFor="apiUrl">API URL (for metadata)</Label>
                <Input
                  id="apiUrl"
                  value={formData.apiUrl}
                  onChange={(e) => setFormData({ ...formData, apiUrl: e.target.value })}
                  placeholder="https://api.example.com/nowplaying"
                />
              </div>

              <div>
                <Label htmlFor="website">Website</Label>
                <Input
                  id="website"
                  value={formData.website}
                  onChange={(e) => setFormData({ ...formData, website: e.target.value })}
                  placeholder="https://station.com"
                />
              </div>

              <div>
                <Label htmlFor="logo">Logo URL</Label>
                <Input
                  id="logo"
                  value={formData.logo}
                  onChange={(e) => setFormData({ ...formData, logo: e.target.value })}
                  placeholder="https://example.com/logo.png"
                />
              </div>

              <div className="flex items-center gap-4">
                <div className="flex items-center space-x-2">
                  <Switch
                    id="active"
                    checked={formData.isActive}
                    onCheckedChange={(checked) => setFormData({ ...formData, isActive: checked })}
                  />
                  <Label htmlFor="active">Active</Label>
                </div>
                <div>
                  <Label htmlFor="sortOrder">Sort Order</Label>
                  <Input
                    id="sortOrder"
                    type="number"
                    value={formData.sortOrder}
                    onChange={(e) => setFormData({ ...formData, sortOrder: parseInt(e.target.value) || 0 })}
                    className="w-20"
                  />
                </div>
              </div>

              <div className="flex gap-2">
                <Button
                  type="submit"
                  disabled={createMutation.isPending || updateMutation.isPending}
                >
                  {editingStation ? 'Update' : 'Create'} Station
                </Button>
                <Button type="button" variant="outline" onClick={resetForm}>
                  Cancel
                </Button>
              </div>
            </form>
          </CardContent>
        </Card>
      )}

      {/* Stations List */}
      <div className="space-y-4">
        {stations?.map((station) => (
          <Card key={station.id}>
            <CardContent className="pt-6">
              <div className="flex items-center justify-between">
                <div className="flex-1">
                  <div className="flex items-center gap-3 mb-2">
                    <h3 className="text-lg font-semibold">{station.name}</h3>
                    <Badge variant={station.isActive ? "default" : "secondary"}>
                      {station.isActive ? "Active" : "Inactive"}
                    </Badge>
                    <Badge variant="outline">{station.apiType}</Badge>
                  </div>
                  <div className="text-sm text-muted-foreground space-y-1">
                    <p><strong>ID:</strong> {station.stationId}</p>
                    {station.frequency && <p><strong>Frequency:</strong> {station.frequency}</p>}
                    {station.location && <p><strong>Location:</strong> {station.location}</p>}
                    {station.genre && <p><strong>Genre:</strong> {station.genre}</p>}
                    {station.description && <p><strong>Description:</strong> {station.description}</p>}
                    <p><strong>Stream URL:</strong> {station.streamUrl}</p>
                    {station.apiUrl && <p><strong>API URL:</strong> {station.apiUrl}</p>}
                  </div>
                </div>
                <div className="flex items-center gap-2">
                  <Button
                    size="sm"
                    variant="outline"
                    onClick={() => handleTest(station.id)}
                    disabled={testMutation.isPending}
                  >
                    <TestTube className="h-4 w-4" />
                  </Button>
                  <Button
                    size="sm"
                    variant="outline"
                    onClick={() => handleEdit(station)}
                  >
                    <Edit className="h-4 w-4" />
                  </Button>
                  <Button
                    size="sm"
                    variant="destructive"
                    onClick={() => handleDelete(station.id)}
                    disabled={deleteMutation.isPending}
                  >
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {(stations?.length || 0) === 0 && (
        <Card>
          <CardContent className="pt-6">
            <div className="text-center text-muted-foreground">
              <Radio className="h-12 w-12 mx-auto mb-4 opacity-50" />
              <p>No radio stations configured yet.</p>
              <p className="mt-2">Click "Add Station" or "Initialize Defaults" to get started.</p>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
};

export default AdminPanel;

