          apiUrl: "https://yield-op-idsync.live.streamtheworld.com/idsync.js?stn=WQHTFM",
          apiType: "streamtheworld",
          stationId: "hot-97",
          frequency: "Hot 97",
          location: "New York",
          genre: "Hip Hop",
          website: "https://hot97.com",
          isActive: true,
