/* Performance optimizations */

html {
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  text-rendering: optimizeLegibility;
  scroll-behavior: smooth;
}

body {
  font-display: swap;
}

/* Hardware acceleration for smooth animations */

.smooth-animation {
  will-change: transform;
  transform: translateZ(0);
}

/* Reduce layout shifts */

img,
video {
  height: auto;
  max-width: 100%;
}

/* Accessibility improvements */

.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border: 0;
}

/* Focus styles for accessibility */

.focus-visible {
  outline: 2px solid var(--color-primary);
  outline-offset: 2px;
}

/* Skip to content link - removed */

:root {
  /* Dynamic theme colors - will be updated by ThemeContext */
  --color-primary: #ff6b35;
  --color-secondary: #d32f2f;
  --color-accent: #ff9800;
  --color-background: #000000;
  --color-surface: #0a0a0a;
  --color-text: #ffffff;
  --color-text-secondary: #cccccc;
  --color-border: #333333;
  --gradient-primary: linear-gradient(135deg, #ff6b35 0%, #d32f2f 100%);
  /* Shadcn variables using theme colors */
  --background: 0 0% 0%;
  --foreground: 0 0% 100%;
  --muted: 0 0% 4%;
  --muted-foreground: 0 0% 80%;
  --popover: 0 0% 4%;
  --popover-foreground: 0 0% 100%;
  --card: 0 0% 4%;
  --card-foreground: 0 0% 100%;
  --border: 0 0% 20%;
  --input: 0 0% 4%;
  --primary: 17 100% 60%;
  --primary-foreground: 0 0% 100%;
  --secondary: 3 88% 50%;
  --secondary-foreground: 0 0% 100%;
  --accent: 37 100% 50%;
  --accent-foreground: 0 0% 100%;
  --destructive: 0 73% 57%;
  --destructive-foreground: 0 0% 98%;
  --ring: 37 100% 50%;
  --radius: 0.75rem;
  /* Legacy metal theme colors for compatibility */
  --metal-orange: var(--color-primary);
  --metal-gold: var(--color-accent);
  --metal-red: var(--color-secondary);
  --metal-blue: hsl(210, 100%, 65%);
}

/* Logo spin easter egg animation */
@keyframes logoSpinFastToSlow {
  0% {
    transform: rotate(0deg);
  }
  20% {
    transform: rotate(720deg);
  }
  100% {
    transform: rotate(1080deg);
  }
}

.logo-spin-easter-egg {
  animation: logoSpinFastToSlow 5s ease-out;
  transform-origin: center;
}

@keyframes scroll {
  0% {
    transform: translateX(0%);
  }

  100% {
    transform: translateX(-50%);
  }
}

@keyframes scroll-smooth {
  from {
    transform: translateX(0%);
  }

  to {
    transform: translateX(-50%);
  }
}

.animate-scroll {
  animation: scroll-smooth 8s linear infinite;
  animation-delay: 1s;
}

.animate-scroll-floating {
  animation: scroll-smooth 4s linear infinite;
  animation-delay: 1s;
}

@keyframes gradientMorph {
  0% {
    background-position: 0% 50%;
  }

  50% {
    background-position: 100% 50%;
  }

  100% {
    background-position: 0% 50%;
  }
}

@keyframes shimmer {
  0% {
    transform: translateX(-100%);
  }

  100% {
    transform: translateX(100%);
  }
}

@keyframes float {
  0%,
  100% {
    transform: translateY(0px) rotate(0deg);
  }

  33% {
    transform: translateY(-10px) rotate(2deg);
  }

  66% {
    transform: translateY(5px) rotate(-1deg);
  }
}

@keyframes glow {
  0%,
  100% {
    box-shadow: 0 0 5px rgba(255, 255, 255, 0.2);
  }

  50% {
    box-shadow: 0 0 20px rgba(255, 255, 255, 0.4);
  }
}

.gradient-morph {
  background-size: 200% 200%;
  animation: gradientMorph 3s ease infinite;
}

.album-float {
  animation: float 6s ease-in-out infinite;
}

.album-glow {
  animation: glow 2s ease-in-out infinite;
}

/* Theme-aware button styling */

.btn-theme-hover:hover {
  background-color: var(--color-primary);
  color: white !important;
  transition: all 0.2s ease;
}

.btn-theme-hover {
  transition: all 0.2s ease;
}

/* Ensure proper text contrast on theme hover */

.dark .btn-theme-hover:hover {
  color: white !important;
}

.light .btn-theme-hover:hover {
  color: white !important;
}

/* Text color fixes for light mode */

.text-adaptive {
  color: hsl(var(--foreground));
}

.dark .text-adaptive {
  color: hsl(var(--foreground));
}

.light {
  /* Light theme colors */
  --background: hsl(0, 0%, 98%);
  --foreground: hsl(0, 0%, 5%);
  --muted: hsl(0, 0%, 95%);
  --muted-foreground: hsl(0, 0%, 45%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(0, 0%, 5%);
  --card: hsl(0, 0%, 100%);
  --card-foreground: hsl(0, 0%, 5%);
  --border: hsl(0, 0%, 88%);
  --input: hsl(0, 0%, 88%);
  --primary: hsl(15, 100%, 60%);
  --primary-foreground: hsl(0, 0%, 98%);
  --secondary: hsl(0, 0%, 96%);
  --secondary-foreground: hsl(0, 0%, 10%);
  --accent: hsl(0, 0%, 96%);
  --accent-foreground: hsl(0, 0%, 10%);
  --destructive: hsl(0, 73%, 57%);
  --destructive-foreground: hsl(0, 0%, 98%);
  --ring: hsl(15, 100%, 60%);
}

/* Scrolling text animation */

@keyframes scroll-left {
  0% {
    transform: translateX(100%);
  }

  100% {
    transform: translateX(-100%);
  }
}

.scrolling-text {
  animation: scroll-left 20s linear infinite;
}

/* Mobile-optimized scrolling */

@keyframes scroll {
  0% {
    transform: translateX(100%);
  }

  100% {
    transform: translateX(-100%);
  }
}

.animate-scroll {
  animation: scroll 15s linear infinite;
}

/* Mobile-responsive improvements */

@media (max-width: 768px) {
  .sticky-player-mobile {
    touch-action: manipulation;
  }

  .sticky-player-mobile .track-text {
    font-size: 14px;
    line-height: 1.3;
  }

  .sticky-player-mobile .volume-control {
    padding: 8px 0;
  }

  /* Improve touch targets */

  .sticky-player-mobile button {
    min-width: 44px;
    min-height: 44px;
  }

  /* Hide scrollbars on mobile */

  .sticky-player-mobile ::-webkit-scrollbar {
    display: none;
  }

  .sticky-player-mobile {
    -ms-overflow-style: none;
    scrollbar-width: none;
