/* Easter Egg: Site Logo Spin Animation */
@keyframes logoSpinFastToSlow {
  0% {
    transform: rotate(0deg);
    animation-timing-function: cubic-bezier(0.25, 0.46, 0.45, 0.94);
  }
  20% {
    transform: rotate(1440deg); /* 4 full rotations rapidly */
    animation-timing-function: cubic-bezier(0.25, 0.46, 0.45, 0.94);
  }
  50% {
    transform: rotate(2520deg); /* 7 full rotations total */
    animation-timing-function: cubic-bezier(0.68, -0.55, 0.265, 1.55);
  }
  80% {
    transform: rotate(3240deg); /* 9 full rotations */
    animation-timing-function: cubic-bezier(0.68, -0.55, 0.265, 1.55);
  }
  100% {
    transform: rotate(3600deg); /* 10 full rotations, ends at original position */
    animation-timing-function: cubic-bezier(0.68, -0.55, 0.265, 1.55);
  }
}

.logo-spin-easter-egg {
