import { Button } from "@/components/ui/button";
import { useTheme } from "@/contexts/ThemeContext";
import FadeInView from "./FadeInView";
import StaggeredAnimation from "./StaggeredAnimation";

export default function About() {
  const { getColors, currentTheme } = useTheme();
  const colors = getColors();

  const scrollToSubscribe = () => {
    const element = document.getElementById("subscribe");
    if (element) {
      element.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <section 
      id="about" 
      className="py-20 transition-colors duration-300" 
      style={{ backgroundColor: colors.background }}
      aria-label="About Spandex Salvation Radio"
    >
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex flex-col lg:flex-row gap-12 items-center">
          <div className="lg:w-3/5">
            <h2 
              className="font-orbitron font-black text-3xl md:text-4xl mb-6"
              style={{ 
                color: currentTheme === 'light-mode' ? '#000000' : colors.text 
              }}
            >
              ABOUT THE REBELLION
            </h2>
            <p 
              className="text-lg font-semibold mb-6"
              style={{ color: colors.textSecondary }}
            >
              Spandex Salvation Radio was born from a passion for the golden era
              of metal music. When hair was big, guitars were loud, and the
              stage was set ablaze with pure rock energy.
            </p>
            <p 
              className="font-semibold mb-6"
              style={{ color: colors.textMuted }}
            >
              We're dedicated to preserving and celebrating the legendary sounds
              of bands like Skid Row, Twisted Sister, M√∂tley Cr√ºe, and countless
              other metal pioneers who defined a generation.
            </p>
            <StaggeredAnimation
              className="flex items-center space-x-6 mb-8"
              staggerDelay={200}
              direction="up"
            >
              <div className="text-center">
                <div
                  className="text-2xl font-black text-center"
                  style={{ color: colors.primary }}
                >
                  24/7
                </div>
                <div 
                  className="text-sm font-semibold text-center"
                  style={{ color: colors.textMuted }}
                >
                  Live Streaming
                </div>
              </div>
              <div className="text-center">
                <div
                  className="text-2xl font-black text-center"
                  style={{ color: colors.primary }}
                >
                  1000+
                </div>
                <div 
                  className="text-sm font-semibold text-center"
                  style={{ color: colors.textMuted }}
                >
                  Metal Tracks
                </div>
              </div>
              <div className="text-center">
                <div
                  className="text-2xl font-black text-center"
                  style={{ color: colors.primary }}
                >
                  50+
                </div>
                <div 
                  className="text-sm font-semibold text-center"
                  style={{ color: colors.textMuted }}
                >
                  Countries
                </div>
              </div>
            </StaggeredAnimation>
            <FadeInView direction="up" delay={600}>
              <Button
                onClick={scrollToSubscribe}
                className="px-6 py-3 rounded-full font-semibold transition-all duration-300 border-0"
                style={{
                  backgroundColor: colors.primary,
                  color: colors.primaryText || "white",
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.backgroundColor =
                    colors.primaryDark || colors.primary;
                  e.currentTarget.style.transform = "scale(1.05)";
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.backgroundColor = colors.primary;
                  e.currentTarget.style.transform = "scale(1)";
                }}
              >
                Join the Revolution
              </Button>
            </FadeInView>
          </div>
          <div className="relative hidden lg:block lg:w-2/5">
            <img
              src="https://images.unsplash.com/photo-1493225457124-a3eb161ffa5f?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&h=600"
              alt="Metal concert with band performing on stage under dramatic lighting"
              className="rounded-xl shadow-2xl w-full h-auto"
            />
            <div className="absolute inset-0 bg-gradient-to-t from-dark-bg/50 to-transparent rounded-xl"></div>
          </div>
        </div>
      </div>
    </section>
  );
}

import { createContext, useContext, useState, useEffect, ReactNode } from 'react';

interface AccessibilitySettings {
  reduceMotion: boolean;
  highContrast: boolean;
  largeText: boolean;
  keyboardNavigation: boolean;
  screenReader: boolean;
}

interface AccessibilityContextType {
  settings: AccessibilitySettings;
  updateSetting: (key: keyof AccessibilitySettings, value: boolean) => void;
  announceToScreenReader: (message: string) => void;
}

const AccessibilityContext = createContext<AccessibilityContextType | undefined>(undefined);

export function useAccessibility() {
  const context = useContext(AccessibilityContext);
  if (!context) {
    throw new Error('useAccessibility must be used within AccessibilityProvider');
  }
  return context;
}

interface AccessibilityProviderProps {
  children: ReactNode;
}

export function AccessibilityProvider({ children }: AccessibilityProviderProps) {
  const [settings, setSettings] = useState<AccessibilitySettings>({
    reduceMotion: false,
    highContrast: false,
    largeText: false,
    keyboardNavigation: true,
    screenReader: false,
  });

  // Detect user preferences from system
  useEffect(() => {
    const detectPreferences = () => {
      const reduceMotion = window.matchMedia('(prefers-reduced-motion: reduce)').matches;
      const highContrast = window.matchMedia('(prefers-contrast: high)').matches;
      const largeText = window.matchMedia('(min-resolution: 2dppx)').matches; // Approximate large text detection

      setSettings(prev => ({
        ...prev,
        reduceMotion,
        highContrast,
      }));

      // Apply global CSS variables based on preferences
      document.documentElement.style.setProperty(
        '--animation-duration',
        reduceMotion ? '0.01ms' : '300ms'
      );

      document.documentElement.style.setProperty(
        '--transition-duration',
        reduceMotion ? '0.01ms' : '200ms'
      );

      if (highContrast) {
        document.documentElement.classList.add('high-contrast');
      } else {
        document.documentElement.classList.remove('high-contrast');
      }
    };

    detectPreferences();

    // Listen for changes in user preferences
    const motionQuery = window.matchMedia('(prefers-reduced-motion: reduce)');
    const contrastQuery = window.matchMedia('(prefers-contrast: high)');

    motionQuery.addEventListener('change', detectPreferences);
    contrastQuery.addEventListener('change', detectPreferences);

    return () => {
      motionQuery.removeEventListener('change', detectPreferences);
      contrastQuery.removeEventListener('change', detectPreferences);
    };
  }, []);

  // Screen reader detection
  useEffect(() => {
    const detectScreenReader = () => {
      // Check for common screen reader indicators
      const hasScreenReader = !!(
        navigator.userAgent.includes('NVDA') ||
        navigator.userAgent.includes('JAWS') ||
        navigator.userAgent.includes('VoiceOver') ||
        window.speechSynthesis
      );

      setSettings(prev => ({
        ...prev,
        screenReader: hasScreenReader
      }));
    };

    detectScreenReader();
  }, []);

  // Apply accessibility settings to DOM
  useEffect(() => {
    const root = document.documentElement;

    // Apply large text scaling
    if (settings.largeText) {
      root.style.fontSize = '120%';
      root.classList.add('large-text');
    } else {
      root.style.fontSize = '';
      root.classList.remove('large-text');
    }

    // Apply keyboard navigation focus styles
    if (settings.keyboardNavigation) {
      root.classList.add('keyboard-navigation');

      // Enhanced focus styles for keyboard navigation
      const style = document.getElementById('keyboard-focus-styles') || document.createElement('style');
      style.id = 'keyboard-focus-styles';
      style.textContent = `
        .keyboard-navigation *:focus {
          outline: 3px solid #f97316 !important;
          outline-offset: 2px !important;
        }
        .keyboard-navigation button:focus,
        .keyboard-navigation input:focus,
        .keyboard-navigation textarea:focus,
        .keyboard-navigation select:focus,
        .keyboard-navigation [role="button"]:focus,
        .keyboard-navigation [tabindex]:focus {
          box-shadow: 0 0 0 3px rgba(249, 115, 22, 0.3) !important;
        }
      `;

      if (!document.getElementById('keyboard-focus-styles')) {
        document.head.appendChild(style);
      }
    } else {
      root.classList.remove('keyboard-navigation');
    }

    // Reduced motion CSS
    if (settings.reduceMotion) {
      const style = document.getElementById('reduced-motion-styles') || document.createElement('style');
      style.id = 'reduced-motion-styles';
      style.textContent = `
        *, *::before, *::after {
          animation-duration: 0.01ms !important;
          animation-iteration-count: 1 !important;
          transition-duration: 0.01ms !important;
          scroll-behavior: auto !important;
        }
      `;

      if (!document.getElementById('reduced-motion-styles')) {
        document.head.appendChild(style);
      }
    } else {
      const existingStyle = document.getElementById('reduced-motion-styles');
      if (existingStyle) {
        existingStyle.remove();
      }
    }

    // High contrast mode
    if (settings.highContrast) {
      root.classList.add('high-contrast');

      const style = document.getElementById('high-contrast-styles') || document.createElement('style');
      style.id = 'high-contrast-styles';
      style.textContent = `
        .high-contrast {
          filter: contrast(200%) !important;
        }
        .high-contrast * {
          background-color: white !important;
          color: black !important;
          border-color: black !important;
        }
        .high-contrast button {
          background-color: black !important;
          color: white !important;
          border: 2px solid white !important;
        }
        .high-contrast a {
          color: blue !important;
          text-decoration: underline !important;
        }
      `;

      if (!document.getElementById('high-contrast-styles')) {
        document.head.appendChild(style);
      }
    } else {
      root.classList.remove('high-contrast');
      const existingStyle = document.getElementById('high-contrast-styles');
      if (existingStyle) {
        existingStyle.remove();
      }
    }
  }, [settings]);

  const updateSetting = (key: keyof AccessibilitySettings, value: boolean) => {
    setSettings(prev => ({
      ...prev,
      [key]: value
    }));
  };

  const announceToScreenReader = (message: string) => {
    // Create or update the live region for screen reader announcements
    let liveRegion = document.getElementById('aria-live-region') as HTMLDivElement;

    if (!liveRegion) {
      liveRegion = document.createElement('div');
      liveRegion.id = 'aria-live-region';
      liveRegion.setAttribute('aria-live', 'polite');
      liveRegion.setAttribute('aria-atomic', 'true');
      liveRegion.style.position = 'absolute';
      liveRegion.style.left = '-10000px';
      liveRegion.style.width = '1px';
      liveRegion.style.height = '1px';
      liveRegion.style.overflow = 'hidden';
      document.body.appendChild(liveRegion);
    }

    // Clear and set new message
    liveRegion.textContent = '';
    setTimeout(() => {
      liveRegion.textContent = message;
    }, 100);
  };

  return (
    <AccessibilityContext.Provider value={{
      settings,
      updateSetting,
      announceToScreenReader
    }}>
      {children}
    </AccessibilityContext.Provider>
  );
}

