                  : `linear-gradient(var(--background), var(--background)) padding-box, linear-gradient(90deg, ${tier.gradientStart}, ${tier.gradientEnd}) border-box`,
                boxShadow: tier.popular 
                  ? "0 0 30px #E520C6, 0 0 60px #F4654F, inset 0 0 20px rgba(229, 32, 198, 0.3)"
                  : `0 0 15px ${tier.gradientStart}40`,
                animation: tier.popular ? "legend-glow 4s linear infinite" : "none"
              }}
            >
              {tier.popular && (
                <>

                  {/* Animated gradient border overlay */}
                  <div
                    className="absolute"
                    style={{
                      top: "-6px",
                      left: "-6px",
                      right: "-6px",
                      bottom: "-6px",
                      background: "linear-gradient(45deg, #ff6b35, #f7931e, #ffcc00, #f7931e, #ff6b35)",
                      backgroundSize: "400% 400%",
                      animation: "gradient-rotate 4s linear infinite",
                      zIndex: -1,
                      borderRadius: "inherit"
                    }}
                  />
                </>
              )}

              <div className="p-6 flex flex-col h-full justify-between">
                <div>
                  <div className="text-center mb-6">
                    <div className="flex justify-center mb-4">
                      <img
                        src={tier.icon}
                        alt={`${tier.name} package icon`}
                        className={`object-contain ${
                          tier.id === "rebel" ? "w-20 h-20" : "w-16 h-16"
                        }`}
                      />
                    </div>

                    <div className="mb-4">
                      <span className="text-3xl font-black text-metal-orange">
                        ${tier.price}
                      </span>
                      <span className="text-gray-400 font-semibold">/month</span>
                    </div>
                  </div>

                  <ul className="space-y-4 mb-12 flex-grow px-6">
                    {(tier.features || []).map((feature, index) => (
                      <li key={index} className="flex items-start">
                        <Check className="text-metal-orange h-6 w-6 mr-3 mt-0.5 flex-shrink-0" />
                        <span className="text-gray-300 font-semibold text-base leading-relaxed">
                          {feature}
                        </span>
                      </li>
                    ))}
                  </ul>
                </div>

                <button
                  onClick={() => handleTierSelect(tier)}
                  className="w-full font-bold rounded-full transition-all duration-300 text-white transform hover:scale-105 hover:shadow-2xl py-3 text-base"
                  style={{
                    fontSize: "16px",
                    background: `linear-gradient(90deg, ${tier.gradientStart}, ${tier.gradientEnd})`,
                    boxShadow: `0 4px 15px ${tier.gradientStart}40`,
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.boxShadow = `0 8px 25px ${tier.gradientStart}80, 0 0 10px ${tier.gradientEnd}60`;
                    e.currentTarget.style.transform = "scale(1.1)";
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.boxShadow = `0 4px 15px ${tier.gradientStart}40`;
                    e.currentTarget.style.transform = "scale(1)";
                  }}
                >
                  Subscribe
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Send, Lock, Crown } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Checkbox } from "@/components/ui/checkbox";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/contexts/AuthContext";
import { useTheme } from "@/contexts/ThemeContext";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import type { Submission, InsertSubmission } from "@shared/schema";
import LiveChat from "@/components/LiveChat";

export default function Submissions() {
  const [formData, setFormData] = useState<InsertSubmission>({
    songTitle: "",
    artistName: "",
    albumTitle: "",
    releaseYear: undefined,
    submitterName: "",
    message: "",
  });
  const [agreedToTerms, setAgreedToTerms] = useState(false);

  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { user, isAuthenticated, isLoading } = useAuth();
  const { getColors, currentTheme } = useTheme();
  const colors = getColors();

  const { data: recentSubmissions = [] } = useQuery<Submission[]>({
    queryKey: ["/api/submissions"],
  });

  const submitMutation = useMutation({
    mutationFn: async (data: InsertSubmission) => {
      // Check for premium subscription before submission
      if (!isAuthenticated) {
        window.location.href = "/#/login";
        return;
      }

      if (!hasPaidSubscription) {
        setShowPremiumNotification(true);
        return;
      }

      const response = await apiRequest("POST", "/api/submissions", data);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Submission Successful!",
        description: "Your song request has been submitted for review.",
      });
      setFormData({
        songTitle: "",
        artistName: "",
        albumTitle: "",
        releaseYear: undefined,
        submitterName: "",
        message: "",
      });
      setAgreedToTerms(false);
      queryClient.invalidateQueries({ queryKey: ["/api/submissions"] });
    },
    onError: () => {
      toast({
        title: "Submission Failed",
        description: "Please check your information and try again.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!agreedToTerms) {
      toast({
        title: "Terms Required",
        description: "Please agree to the terms and conditions.",
        variant: "destructive",
      });
      return;
    }
    submitMutation.mutate(formData);
  };

  const handleInputChange = (
    field: keyof InsertSubmission,
    value: string | number | undefined,
  ) => {
    setFormData((prev) => ({ ...prev, [field]: value }));
  };

  const formatDate = (date: string | Date) => {
    return new Date(date).toLocaleDateString("en-US", {
      month: "short",
      day: "numeric",
    });
  };

  // State for premium notification
  const [showPremiumNotification, setShowPremiumNotification] = useState(false);

  // Check if user has paid subscription (assuming stripeSubscriptionId indicates paid status)
  const hasPaidSubscription = (user as any)?.stripeSubscriptionId || false;

  return (
    <section 
      id="submissions" 
      className="py-20 transition-colors duration-300"
      style={{ backgroundColor: colors.background }}
    >
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-12">
          <h2 
            className="font-orbitron font-bold text-3xl md:text-4xl mb-4"
            style={{ 
              color: currentTheme === 'light-mode' ? '#000000' : colors.text 
            }}
          >
            SUBMIT YOUR REQUESTS
          </h2>
          <p 
            className="text-lg"
            style={{ color: colors.textMuted }}
          >
            Got a metal track that needs to be heard? Submit your requests and
            help shape our playlist.
          </p>
        </div>

        {!isAuthenticated || !hasPaidSubscription ? (
          <div className="mt-6">
            <div
              className="p-6 rounded-lg text-center transition-all duration-300 animate-in fade-in slide-in-from-top-4"
              style={{
                backgroundColor: `${colors.primary}25`,
                borderColor: `${colors.primary}30`,
                border: `1px solid ${colors.primary}30`,
              }}
            >
              <div
                className="flex items-center justify-center gap-2 mb-4"
                style={{ color: colors.primary }}
              >
                <Crown className="h-5 w-5" />
                <span className="font-semibold">
                  Premium Feature - Paid Subscription Required
                </span>
              </div>
              <p 
                className="mb-6"
                style={{ color: colors.textMuted }}
              >
                Song submissions are available exclusively to paid subscribers.
                Sign in and upgrade to submit your favorite tracks.
              </p>
              <div className="flex gap-4 justify-center">
                <Button
                  onClick={() => {
                    // Trigger auth modal instead of redirect
                    const event = new CustomEvent("openAuthModal", {
                      detail: { mode: "login" },
                    });
                    window.dispatchEvent(event);
                  }}
                  className="px-6 py-3 rounded-full font-semibold transition-all duration-300 border-0"
                  style={{
                    backgroundColor: colors.primary,
                    color: colors.primaryText || "white",
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.backgroundColor =
                      colors.primaryDark || colors.primary;
                    e.currentTarget.style.transform = "scale(1.05)";
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.backgroundColor = colors.primary;
                    e.currentTarget.style.transform = "scale(1)";
                  }}
                >
                  Login
                </Button>
                <Button
                  onClick={() =>
                    document
                      .getElementById("subscribe")
                      ?.scrollIntoView({ behavior: "smooth" })
                  }
                  variant="outline"
                  className="px-6 py-3 rounded-full font-semibold transition-all duration-300"
                  style={{
                    borderColor: colors.primary,
                    color: colors.primary,
                    backgroundColor: "transparent",
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.backgroundColor = `${colors.primary}10`;
                    e.currentTarget.style.transform = "scale(1.05)";
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.backgroundColor = "transparent";
                    e.currentTarget.style.transform = "scale(1)";
                  }}
                >
                  View Subscriptions
                </Button>
              </div>
            </div>
          </div>
        ) : (
          <Card className="bg-dark-surface border-dark-border mb-12">
            <CardContent className="p-8">
              <form onSubmit={handleSubmit} className="space-y-6">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <Label
                      htmlFor="songTitle"
                      className="text-gray-300 font-semibold"
                    >
                      Song Title *
                    </Label>
                    <Input
                      id="songTitle"
                      value={formData.songTitle}
                      onChange={(e) =>
                        handleInputChange("songTitle", e.target.value)
                      }
                      placeholder="Enter song title"
                      required
                      className="bg-dark-bg border-dark-border text-white placeholder-gray-500 focus:border-primary"
                    />
                  </div>
                  <div>
                    <Label
                      htmlFor="artistName"
                      className="text-gray-300 font-semibold"
                    >
                      Artist/Band *
                    </Label>
                    <Input
                      id="artistName"
                      value={formData.artistName}
                      onChange={(e) =>
                        handleInputChange("artistName", e.target.value)
                      }
                      placeholder="Enter artist name"
                      required
                      className="bg-dark-bg border-dark-border text-white placeholder-gray-500 focus:border-primary"
                    />
                  </div>
                </div>

                <div>
                  <Label
                    htmlFor="submitterName"
                    className="text-gray-300 font-semibold"
                  >
                    Your Name
                  </Label>
                  <Input
                    id="submitterName"
                    value={formData.submitterName || ""}
                    onChange={(e) =>
                      handleInputChange("submitterName", e.target.value)
                    }
                    placeholder="Enter your name (optional)"
                    className="bg-dark-bg border-dark-border text-white placeholder-gray-400/60 focus:border-primary"
                  />
                </div>

                <div>
                  <Label
                    htmlFor="message"
                    className="text-gray-300 font-semibold"
                  >
                    Additional Message
                  </Label>
                  <Textarea
                    id="message"
                    value={formData.message || ""}
                    onChange={(e) =>
                      handleInputChange("message", e.target.value)
                    }
                    placeholder="Tell us why this song rocks or share any additional details..."
                    rows={4}
                    className="bg-dark-bg border-dark-border text-white placeholder-gray-400/60 focus:border-primary resize-none"
                  />
                </div>

                <div className="flex items-center space-x-2">
                  <Checkbox
                    id="terms"
                    checked={agreedToTerms}
                    onCheckedChange={(checked) => setAgreedToTerms(checked === true)}
                    className="border-dark-border"
                  />
                  <Label htmlFor="terms" className="text-gray-400 text-sm">
                    I agree that this submission follows metal music guidelines
                    and community standards.
                  </Label>
                </div>

                <Button
                  type="submit"
                  disabled={submitMutation.isPending}
                  className="w-full md:w-auto bg-metal-orange hover:bg-orange-600 text-white px-8 py-3 rounded-full font-bold text-lg transition-all duration-300 animate-glow"
                >
                  <Send className="mr-2 h-4 w-4" />
                  {submitMutation.isPending
                    ? "SUBMITTING..."
                    : "SUBMIT REQUEST"}
                </Button>
              </form>
            </CardContent>
          </Card>
        )}

        {/* Recent Submissions */}
        {(recentSubmissions || []).length > 0 &&
          isAuthenticated &&
          hasPaidSubscription && (
            <div className="mt-16">
              <h3
                className="font-bold text-xl mb-6"
                style={{ color: "var(--color-primary)" }}
              >
                Recent Submissions
              </h3>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {(recentSubmissions || []).slice(0, 4).map((submission) => (
                  <Card
                    key={submission.id}
                    className="bg-dark-surface border-dark-border"
                  >
                    <CardContent className="p-4">
                      <div className="flex items-start justify-between mb-2">
                        <div>
                          <h4 className="font-semibold">
                            {submission.songTitle}
                          </h4>
                          <p className="text-gray-400 text-sm">
                            {submission.artistName}
                          </p>
                        </div>
                        <span
                          className={`text-xs px-2 py-1 rounded ${
                            submission.status === "approved"
                              ? "text-metal-orange bg-metal-orange/20"
                              : submission.status === "rejected"
                                ? "text-metal-red bg-metal-red/20"
                                : "text-metal-gold bg-metal-gold/20"
                          }`}
                        >
                          {submission.status === "approved"
                            ? "Approved"
                            : submission.status === "rejected"
                              ? "Rejected"
                              : "Pending"}
                        </span>
                      </div>
                      <p className="text-gray-500 text-xs">
                        {submission.submitterName
                          ? `Submitted by: ${submission.submitterName}`
                          : "Anonymous"}
                      </p>
                      <p className="text-gray-500 text-xs">
                        {submission.createdAt
                          ? formatDate(submission.createdAt)
                          : "Recently"}
                      </p>
                    </CardContent>
                  </Card>
                ))}
              </div>
            </div>
          )}

        {/* Premium Feature Notification */}
        {showPremiumNotification && (
          <LiveChat
            isEnabled={true}
            onToggle={() => setShowPremiumNotification(false)}
            premiumFeatureType="submission"
          />
        )}
      </div>
    </section>
  );
}

