import { createContext, useContext, useState, ReactNode } from "react";

interface AdminUser {
  id: number;
  username: string;
  isAdmin: boolean;
}

interface AdminContextType {
  user: AdminUser | null;
  isAdmin: boolean;
  login: (username: string, password: string) => Promise<boolean>;
  logout: () => void;
}

const AdminContext = createContext<AdminContextType | undefined>(undefined);

export function AdminProvider({ children }: { children: ReactNode }) {
  const [user, setUser] = useState<AdminUser | null>(null);

  const login = async (
    username: string,
    password: string,
  ): Promise<boolean> => {
    try {
      const response = await fetch("/api/auth/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ username, password }),
      });

      if (response.ok) {
        const userData = await response.json();
        setUser(userData);
        return true;
      }
      return false;
    } catch (error) {
      console.error("Login error:", error);
      return false;
    }
  };

  const logout = () => {
    setUser(null);
  };

  return (
    <AdminContext.Provider
      value={{
        user,
        isAdmin: user?.isAdmin || false,
        login,
        logout,
      }}
    >
      {children}
    </AdminContext.Provider>
  );
}

export function useAdmin() {
  const context = useContext(AdminContext);
  if (context === undefined) {
    throw new Error("useAdmin must be used within an AdminProvider");
  }
  return context;
}

import {
  createContext,
  useContext,
  useState,
  useEffect,
  ReactNode,
} from "react";
import { User } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { auth, handleRedirectResult, signInWithGoogle, createUserProfile, getUserProfile, updateUserProfile, uploadProfileImage, updateListeningStatus, updateUserLocation, loginUser, registerUser } from "@/lib/firebase";
import { onAuthStateChanged, signOut } from "firebase/auth";

interface AuthContextType {
  user: User | null;
  firebaseUser: any | null;
  firebaseProfile: any | null;
  loading: boolean;
  isAuthenticated: boolean;
  login: (email: string, password: string) => Promise<void>;
  register: (
    email: string,
    password: string,
    username: string,
    firstName: string,
    lastName: string,
    phoneNumber: string,
    recaptchaToken?: string,
  ) => Promise<void>;
  logout: () => Promise<void>;
  refreshUser: () => Promise<void>;
  signInWithGoogle: () => Promise<void>;
  updateProfile: (updates: any) => Promise<boolean>;
  uploadProfileImage: (file: File) => Promise<boolean>;
  updateListeningStatus: (isListening: boolean) => Promise<void>;
  updateLocation: () => Promise<void>;
}

export const AuthContext = createContext<AuthContextType | undefined>(
  undefined,
);

export function useAuth() {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
}

export function AuthProvider({ children }: { children: ReactNode }) {
  const [user, setUser] = useState<User | null>(null);
  const [firebaseUser, setFirebaseUser] = useState<any | null>(null);
  const [firebaseProfile, setFirebaseProfile] = useState<any | null>(null);
  const [loading, setLoading] = useState(true);

  // Handle Firebase user profile loading
  const loadFirebaseProfile = async (userId: string) => {
    try {
      const result = await getUserProfile(userId);
      if (result.success) {
        setFirebaseProfile(result.profile);
        return result.profile;
      }
      return null;
    } catch (error) {
      console.error('Error loading Firebase profile:', error);
      return null;
    }
  };

  // Refresh user from Firebase only
  const refreshUser = async () => {
    try {
      // Use Firebase auth state instead of server API
      const currentUser = auth.currentUser;
      if (currentUser) {
        setUser({
          id: currentUser.uid,
          email: currentUser.email || '',
          passwordHash: '', // Not available from Firebase auth
          firstName: currentUser.displayName?.split(' ')[0] || '',
          lastName: currentUser.displayName?.split(' ').slice(1).join(' ') || '',
          profileImageUrl: currentUser.photoURL || '',
          username: currentUser.displayName || '',
          phoneNumber: currentUser.phoneNumber || '',
          googleId: currentUser.uid,
          location: null,
          isActiveListening: false,
          activeSubscription: false,
          renewalDate: null,
          isPhoneVerified: !!currentUser.phoneNumber,
          showVerifiedBadge: false,
          accountDeletionScheduled: false,
          accountDeletionDate: null,
          isEmailVerified: currentUser.emailVerified,
          emailVerificationToken: null,
          phoneVerificationCode: null,
          resetPasswordToken: null,
          resetPasswordExpires: null,
          isAdmin: false,
          stripeCustomerId: null,
          stripeSubscriptionId: null,
          subscriptionStatus: null,
          subscriptionTier: null,
          lastLoginAt: null,
          isFirstLogin: true,
          createdAt: new Date(currentUser.metadata.creationTime || new Date()),
          updatedAt: new Date(currentUser.metadata.lastSignInTime || new Date()),
        });
      } else {
        setUser(null);
      }
    } catch (error) {
      console.error('Error refreshing user:', error);
      setUser(null);
    } finally {
      setLoading(false);
    }
  };

  const login = async (email: string, password: string) => {
    try {
      console.log('[AuthContext] Attempting email/password login...');
      const result = await loginUser(email, password);

      if (!result.success) {
        throw new Error(result.error || "Login failed");
      }

      console.log('[AuthContext] Login successful:', result.userID);

      // Store user data in localStorage
      localStorage.setItem('userID', result.userID);
      localStorage.setItem('userProfile', JSON.stringify(result.profile));
      localStorage.setItem('isLoggedIn', 'true');

      // Set user state
      setUser({
        id: result.userID,
        email: result.profile.EmailAddress,
        username: `${result.profile.FirstName} ${result.profile.LastName}`.trim(),
        firstName: result.profile.FirstName,
        lastName: result.profile.LastName,
        phoneNumber: result.profile.PhoneNumber,
        isEmailVerified: true,
        isPhoneVerified: !!result.profile.PhoneNumber,
        createdAt: result.profile.CreatedAt || new Date().toISOString(),
        updatedAt: result.profile.UpdatedAt || new Date().toISOString(),
      });

      setFirebaseProfile(result.profile);

      console.log('[AuthContext] Login completed, user state updated');
    } catch (error: any) {
      console.error('[AuthContext] Login error:', error);
      throw new Error(error.message || "Login failed");
    }
  };

  const register = async (
    email: string,
    password: string,
    username: string,
    firstName: string,
    lastName: string,
    phoneNumber: string,
    recaptchaToken?: string,
  ) => {
    try {
      console.log('[AuthContext] Attempting email/password registration...');
      const result = await registerUser({
        firstName,
        lastName,
        email,
        phoneNumber,
        password,
      });

      if (!result.success) {
        throw new Error(result.error || "Registration failed");
      }

      console.log('[AuthContext] Registration successful:', result.userID);

      // Store user data in localStorage
      localStorage.setItem('userID', result.userID);
      localStorage.setItem('userProfile', JSON.stringify(result.profile));
      localStorage.setItem('isLoggedIn', 'true');

      // Set user state
      setUser({
        id: result.userID,
        email: result.profile.EmailAddress,
        username: `${result.profile.FirstName} ${result.profile.LastName}`.trim(),
        firstName: result.profile.FirstName,
        lastName: result.profile.LastName,
        phoneNumber: result.profile.PhoneNumber,
        isEmailVerified: true,
        isPhoneVerified: !!result.profile.PhoneNumber,
        createdAt: result.profile.CreatedAt || new Date().toISOString(),
        updatedAt: result.profile.UpdatedAt || new Date().toISOString(),
      });

      setFirebaseProfile(result.profile);

      console.log('[AuthContext] Registration completed, user state updated');
    } catch (error: any) {
      console.error('[AuthContext] Registration error:', error);
      throw new Error(error.message || "Registration failed");
    }
  };

  const logout = async () => {
    try {
      await signOut(auth);
      setUser(null);
      setFirebaseUser(null);
      setFirebaseProfile(null);

      // Clear localStorage
      localStorage.removeItem('userID');
      localStorage.removeItem('userProfile');
      localStorage.removeItem('isLoggedIn');
    } catch (error) {
      console.error('Logout error:', error);
    }
  };

  // Firebase Google Sign In
  const handleGoogleSignIn = async () => {
    try {
      await signInWithGoogle();
    } catch (error) {
      console.error('Google sign in error:', error);
      throw error;
    }
  };

  // Update Firebase profile
  const updateProfile = async (updates: any) => {
    try {
      if (firebaseProfile?.UserID) {
        const result = await updateUserProfile(firebaseProfile.UserID, updates);
        if (result.success) {
          // Reload the profile to reflect changes
          await loadFirebaseProfile(firebaseProfile.UserID);
          return true;
        }
      }
      return false;
    } catch (error) {
      console.error('Error updating profile:', error);
      return false;
    }
  };

  // Upload profile image
  const handleUploadProfileImage = async (file: File) => {
    try {
      if (firebaseProfile?.UserID) {
        const result = await uploadProfileImage(firebaseProfile.UserID, file);
        if (result.success) {
          // Reload the profile to reflect changes
          await loadFirebaseProfile(firebaseProfile.UserID);
          return true;
        }
      }
      return false;
    } catch (error) {
      console.error('Error uploading profile image:', error);
      return false;
    }
  };

  // Update listening status
  const handleUpdateListeningStatus = async (isListening: boolean) => {
    try {
      if (firebaseProfile?.UserID) {
        await updateListeningStatus(firebaseProfile.UserID, isListening);
      }
    } catch (error) {
      console.error('Error updating listening status:', error);
    }
  };

  // Update location
  const handleUpdateLocation = async () => {
    try {
      if (firebaseProfile?.UserID) {
        await updateUserLocation(firebaseProfile.UserID);
        // Reload the profile to reflect changes
        await loadFirebaseProfile(firebaseProfile.UserID);
      }
    } catch (error) {
      console.error('Error updating location:', error);
    }
  };

  useEffect(() => {
    console.log('[AuthContext] Setting up authentication listeners...');

    // Restore login state from localStorage
    const wasLoggedIn = localStorage.getItem('isLoggedIn');
    const userID = localStorage.getItem('userID');
    const userProfile = localStorage.getItem('userProfile');

    console.log('[AuthContext] Previous login state:', wasLoggedIn);
    console.log('[AuthContext] Stored userID:', userID);
    console.log('[AuthContext] Stored userProfile:', userProfile);

    if (wasLoggedIn === 'true' && userID && userProfile) {
      try {
        const profile = JSON.parse(userProfile);
        setUser({
          id: userID,
          email: profile.EmailAddress,
          username: `${profile.FirstName} ${profile.LastName}`.trim(),
          firstName: profile.FirstName,
          lastName: profile.LastName,
          phoneNumber: profile.PhoneNumber,
          isEmailVerified: true,
          isPhoneVerified: !!profile.PhoneNumber,
          createdAt: new Date().toISOString(),
          updatedAt: new Date().toISOString(),
        });
        setFirebaseProfile(profile);
        console.log('[AuthContext] Restored user from localStorage:', profile);
      } catch (error) {
        console.error('[AuthContext] Error parsing stored profile:', error);
        // Clear invalid data
        localStorage.removeItem('userID');
        localStorage.removeItem('userProfile');
        localStorage.removeItem('isLoggedIn');
      }
    }

    // Set up Firebase auth state listener for Google OAuth
    const unsubscribe = onAuthStateChanged(auth, async (user) => {
      console.log('[AuthContext] onAuthStateChanged fired. User:', user);
      if (user) {
        console.log('[AuthContext] User authenticated via Google:', {
          uid: user.uid,
          email: user.email,
          displayName: user.displayName,
          emailVerified: user.emailVerified,
          providerId: user.providerData[0]?.providerId
        });

        setFirebaseUser(user);
        localStorage.setItem('isLoggedIn', 'true');

        // Set the user state for the app
        setUser({
          id: user.uid,
          email: user.email || '',
          username: user.displayName || '',
          firstName: user.displayName?.split(' ')[0] || '',
          lastName: user.displayName?.split(' ').slice(1).join(' ') || '',
          phoneNumber: user.phoneNumber || '',
          isEmailVerified: user.emailVerified,
          isPhoneVerified: !!user.phoneNumber,
          createdAt: user.metadata.creationTime || new Date().toISOString(),
          updatedAt: user.metadata.lastSignInTime || new Date().toISOString(),
        });

        try {
          const profileResult = await createUserProfile(user);
          console.log('[AuthContext] Profile creation result:', profileResult);
          if (profileResult.success) {
            setFirebaseProfile(profileResult.profile);
            console.log('[AuthContext] Firebase profile set:', profileResult.profile);
          }
        } catch (error) {
          console.error('[AuthContext] Error creating/loading profile:', error);
        }
      } else {
        console.log('[AuthContext] No user authenticated, clearing state');
        setFirebaseUser(null);
        setFirebaseProfile(null);
        setUser(null);
        localStorage.removeItem('isLoggedIn');
      }
      setLoading(false);
    });

    // Handle redirect result from Google sign in
    console.log('[AuthContext] Checking for redirect result...');
    handleRedirectResult().then((result) => {
      console.log('[AuthContext] handleRedirectResult result:', result);
      if (result.success && result.user) {
        console.log('[AuthContext] Successful redirect result, setting user state');
        setFirebaseUser(result.user);
        setUser({
          id: result.user.uid,
          email: result.user.email || '',
          username: result.user.displayName || '',
          firstName: result.user.displayName?.split(' ')[0] || '',
          lastName: result.user.displayName?.split(' ').slice(1).join(' ') || '',
          phoneNumber: result.user.phoneNumber || '',
          isEmailVerified: result.user.emailVerified,
          isPhoneVerified: !!result.user.phoneNumber,
          createdAt: result.user.metadata.creationTime || new Date().toISOString(),
          updatedAt: result.user.metadata.lastSignInTime || new Date().toISOString(),
        });
        if (result.profileResult?.profile) {
          setFirebaseProfile(result.profileResult.profile);
        }
        setLoading(false);
        localStorage.setItem('isLoggedIn', 'true');
        console.log('[AuthContext] User state set after Google redirect:', result.user);
      } else {
        console.log('[AuthContext] No successful redirect result, error:', result.error);
      }
    });

    setLoading(false);

    return () => {
      unsubscribe();
    };
  }, []);

  return (
    <AuthContext.Provider
      value={{
        user,
        firebaseUser,
        firebaseProfile,
        loading,
        isAuthenticated: !!user || !!firebaseUser,
        login,
        register,
        logout,
        refreshUser,
        signInWithGoogle: handleGoogleSignIn,
        updateProfile,
        uploadProfileImage: handleUploadProfileImage,
        updateListeningStatus: handleUpdateListeningStatus,
        updateLocation: handleUpdateLocation,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
}

import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';

export interface FirebaseUser {
  userID: string;
  firstName: string;
  lastName: string;
  email: string;
  phoneNumber?: string;
  profileImage: string;
  location?: {
    lat: number;
    lng: number;
    address?: string;
  };
  activeSubscription: boolean;
  isActiveListening: boolean;
  renewalDate?: Date;
  createdAt: Date;
  lastLoginAt?: Date;
}

interface FirebaseAuthContextType {
  user: FirebaseUser | null;
  isLoading: boolean;
  isAuthenticated: boolean;
  register: (data: {
    firstName: string;
    lastName: string;
    email: string;
    phoneNumber?: string;
    password: string;
  }) => Promise<{ success: boolean; message: string; userKey?: string }>;
  login: (email: string, password: string) => Promise<{ success: boolean; message: string }>;
  loginWithGoogle: (data: {
    googleId: string;
    email: string;
    firstName: string;
    lastName: string;
  }) => Promise<{ success: boolean; message: string }>;
  logout: () => Promise<void>;
  updateProfile: (data: Partial<{
    firstName: string;
    lastName: string;
    phoneNumber: string;
    location: { lat: number; lng: number; address?: string };
    isActiveListening: boolean;
    activeSubscription: boolean;
  }>) => Promise<{ success: boolean; message: string }>;
  refreshUser: () => Promise<void>;
}

const FirebaseAuthContext = createContext<FirebaseAuthContextType | undefined>(undefined);

export function useFirebaseAuth() {
  const context = useContext(FirebaseAuthContext);
  if (context === undefined) {
    throw new Error('useFirebaseAuth must be used within a FirebaseAuthProvider');
  }
  return context;
}

interface FirebaseAuthProviderProps {
  children: ReactNode;
}

export function FirebaseAuthProvider({ children }: FirebaseAuthProviderProps) {
  const [user, setUser] = useState<FirebaseUser | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Check authentication status on mount
  useEffect(() => {
    checkAuthStatus();
  }, []);

  const checkAuthStatus = async () => {
    try {
      const response = await fetch('/api/auth/firebase/user', {
        credentials: 'include'
      });

      if (response.ok) {
        const data = await response.json();
        if (data.authenticated && data.user) {
          setUser(data.user);
          setIsAuthenticated(true);
        }
      }
    } catch (error) {
      console.error('Auth status check failed:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const register = async (data: {
    firstName: string;
    lastName: string;
    email: string;
    phoneNumber?: string;
    password: string;
  }) => {
    try {
      const response = await fetch('/api/auth/firebase/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify(data),
      });

      const result = await response.json();

      if (response.ok) {
        return { success: true, message: result.message, userKey: result.userKey };
      } else {
        return { success: false, message: result.message || 'Registration failed' };
      }
    } catch (error) {
      console.error('Registration error:', error);
      return { success: false, message: 'Registration failed due to network error' };
    }
  };

  const login = async (email: string, password: string) => {
    try {
      const response = await fetch('/api/auth/firebase/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({ email, password }),
      });

      const result = await response.json();

      if (response.ok) {
        setUser(result.user);
        setIsAuthenticated(true);
        return { success: true, message: result.message };
      } else {
        return { success: false, message: result.message || 'Login failed' };
      }
    } catch (error) {
      console.error('Login error:', error);
      return { success: false, message: 'Login failed due to network error' };
    }
  };

  const loginWithGoogle = async (data: {
    googleId: string;
    email: string;
    firstName: string;
    lastName: string;
  }) => {
    try {
      const response = await fetch('/api/auth/firebase/google', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify(data),
      });

      const result = await response.json();

      if (response.ok) {
        setUser(result.user);
        setIsAuthenticated(true);
        return { success: true, message: result.message };
      } else {
        return { success: false, message: result.message || 'Google authentication failed' };
      }
    } catch (error) {
      console.error('Google login error:', error);
      return { success: false, message: 'Google authentication failed due to network error' };
    }
  };

  const logout = async () => {
    try {
      await fetch('/api/auth/firebase/logout', {
        method: 'POST',
        credentials: 'include',
      });
    } catch (error) {
      console.error('Logout error:', error);
    } finally {
      setUser(null);
      setIsAuthenticated(false);
    }
  };

  const updateProfile = async (data: Partial<{
    firstName: string;
    lastName: string;
    phoneNumber: string;
    location: { lat: number; lng: number; address?: string };
    isActiveListening: boolean;
    activeSubscription: boolean;
  }>) => {
    try {
      const response = await fetch('/api/auth/firebase/update-profile', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify(data),
      });

      const result = await response.json();

      if (response.ok) {
        // Refresh user data after successful update
        await refreshUser();
        return { success: true, message: result.message };
      } else {
        return { success: false, message: result.message || 'Profile update failed' };
      }
    } catch (error) {
      console.error('Profile update error:', error);
      return { success: false, message: 'Profile update failed due to network error' };
    }
  };

  const refreshUser = async () => {
    try {
      const response = await fetch('/api/auth/firebase/user', {
        credentials: 'include'
      });

      if (response.ok) {
        const data = await response.json();
        if (data.authenticated && data.user) {
          setUser(data.user);
          setIsAuthenticated(true);
        } else {
          setUser(null);
          setIsAuthenticated(false);
        }
      }
    } catch (error) {
      console.error('User refresh failed:', error);
    }
  };

  const value = {
    user,
    isLoading,
    isAuthenticated,
    register,
    login,
    loginWithGoogle,
    logout,
    updateProfile,
    refreshUser,
  };

  return (
    <FirebaseAuthContext.Provider value={value}>
      {children}
    </FirebaseAuthContext.Provider>
  );
}

import {
  createContext,
  useContext,
  useState,
  useRef,
  useEffect,
  ReactNode,
  useCallback,
} from "react";
// Radio station interface
interface RadioStation {
  id: string;
  stationId?: string;
  name: string;
  frequency: string;
  location: string;
  genre: string;
  streamUrl: string;
  description: string;
  icon: string;
}

interface TrackInfo {
  title: string;
  artist: string;
  album?: string;
  artwork?: string;
  isAd?: boolean;
  adCompany?: string;
  adReason?: string;
  stationName?: string;
  frequency?: string;
  location?: string;
  genre?: string;
  lastUpdated?: Date;
}

interface RadioContextType {
  isPlaying: boolean;
  volume: number;
  isMuted: boolean;
  isLoading: boolean;
  error: string | null;
  currentTrack: TrackInfo;
  currentStation: RadioStation | null;
  stationName: string;
  isTransitioning: boolean;
  isAdPlaying: boolean;
  adInfo: {
    company?: string;
    reason?: string;
    artwork?: string;
  };
  togglePlayback: () => Promise<void>;
  setVolume: (volume: number) => void;
  toggleMute: () => void;
  changeStation: (station: RadioStation) => Promise<void>;
  setCurrentTrack: (track: TrackInfo) => void;
  audioRef: React.RefObject<HTMLAudioElement>;
}

const RadioContext = createContext<RadioContextType | undefined>(undefined);

