import { useEffect, useState, RefObject } from 'react';

interface UseIntersectionObserverOptions {
  threshold?: number;
  rootMargin?: string;
  root?: Element | null;
}

export function useIntersectionObserver(
  ref: RefObject<Element>,
  options: UseIntersectionObserverOptions = {}
): boolean {
  const [isIntersecting, setIsIntersecting] = useState(false);

  useEffect(() => {
    const element = ref.current;
    if (!element) return;

    const observer = new IntersectionObserver(
      ([entry]) => {
        setIsIntersecting(entry.isIntersecting);
      },
      {
        threshold: options.threshold || 0.1,
        rootMargin: options.rootMargin || '0px',
        root: options.root || null,
      }
    );

    observer.observe(element);

    return () => {
      observer.unobserve(element);
    };
  }, [ref, options.threshold, options.rootMargin, options.root]);

  return isIntersecting;
}

import { useEffect, useRef, useState } from 'react';

interface UseIntersectionObserverProps {
  threshold?: number;
  rootMargin?: string;
}

export function useIntersectionObserver({
  threshold = 0.1,
  rootMargin = '0px'
}: UseIntersectionObserverProps = {}) {
  const ref = useRef<HTMLDivElement>(null);
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsVisible(true);
          // Once visible, stop observing
          if (ref.current) {
            observer.unobserve(ref.current);
          }
        }
      },
      {
        threshold,
        rootMargin
      }
    );

    if (ref.current) {
      observer.observe(ref.current);
    }

    return () => {
      if (ref.current) {
        observer.unobserve(ref.current);
      }
    };
  }, [threshold, rootMargin]);

  return { ref, isVisible };
}

import * as React from "react";

const MOBILE_BREAKPOINT = 768;

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(
    undefined,
  );

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`);
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT);
    };
    mql.addEventListener("change", onChange);
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT);
    return () => mql.removeEventListener("change", onChange);
  }, []);

  return !!isMobile;
}

import { useEffect, useRef, useState } from 'react';

export interface ScrollVelocityData {
  velocity: number;
  direction: 'up' | 'down' | 'none';
  isScrolling: boolean;
}

export function useScrollVelocity(): ScrollVelocityData {
  const [velocity, setVelocity] = useState(0);
  const [direction, setDirection] = useState<'up' | 'down' | 'none'>('none');
  const [isScrolling, setIsScrolling] = useState(false);

  const lastScrollY = useRef(0);
  const lastTimestamp = useRef(0);
  const scrollTimeout = useRef<NodeJS.Timeout>();

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;
      const currentTimestamp = Date.now();

      // Calculate velocity (pixels per millisecond)
      const timeDelta = currentTimestamp - lastTimestamp.current;
      const scrollDelta = currentScrollY - lastScrollY.current;

      if (timeDelta > 0) {
        const currentVelocity = Math.abs(scrollDelta) / timeDelta;
        setVelocity(currentVelocity);

        // Determine direction
        if (scrollDelta > 0) {
          setDirection('down');
        } else if (scrollDelta < 0) {
          setDirection('up');
        } else {
          setDirection('none');
        }

        setIsScrolling(true);

        // Clear existing timeout
        if (scrollTimeout.current) {
          clearTimeout(scrollTimeout.current);
        }

        // Set scrolling to false after 150ms of no scroll
        scrollTimeout.current = setTimeout(() => {
          setIsScrolling(false);
          setVelocity(0);
          setDirection('none');
        }, 150);
      }

      lastScrollY.current = currentScrollY;
      lastTimestamp.current = currentTimestamp;
    };

    // Initialize
    lastScrollY.current = window.scrollY;
    lastTimestamp.current = Date.now();

    window.addEventListener('scroll', handleScroll, { passive: true });

    return () => {
      window.removeEventListener('scroll', handleScroll);
      if (scrollTimeout.current) {
        clearTimeout(scrollTimeout.current);
      }
    };
  }, []);

  return { velocity, direction, isScrolling };
}

// Helper function to calculate animation duration based on scroll velocity
export function getAdaptiveAnimationDuration(
  baselineDuration: number,
  velocity: number,
  minDuration: number = 150,
  maxDuration?: number
): number {
  // Define velocity thresholds
  const slowVelocity = 0.3;   // Slow scrolling
  const fastVelocity = 1.5;   // Fast scrolling

  let adaptiveDuration = baselineDuration;

  if (velocity > fastVelocity) {
    // Fast scrolling: reduce duration by up to 50%
    const speedMultiplier = Math.min(velocity / fastVelocity, 2);
    adaptiveDuration = baselineDuration * (0.5 + (0.5 / speedMultiplier));
  } else if (velocity > slowVelocity) {
    // Normal scrolling: use baseline duration
    adaptiveDuration = baselineDuration;
  } else {
    // Slow scrolling: use baseline duration (never slower)
    adaptiveDuration = baselineDuration;
  }

  // Ensure we don't go below minimum duration
  adaptiveDuration = Math.max(adaptiveDuration, minDuration);

  // Ensure we don't exceed maximum duration if specified
  if (maxDuration) {
    adaptiveDuration = Math.min(adaptiveDuration, maxDuration);
  }

  return Math.round(adaptiveDuration);
}

import * as React from "react";

import type { ToastActionElement, ToastProps } from "@/components/ui/toast";

const TOAST_LIMIT = 1;
const TOAST_REMOVE_DELAY = 1000000;

type ToasterToast = ToastProps & {
  id: string;
  title?: React.ReactNode;
  description?: React.ReactNode;
  action?: ToastActionElement;
};

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const;

let count = 0;

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER;
  return count.toString();
}

type ActionType = typeof actionTypes;

type Action =
  | {
      type: ActionType["ADD_TOAST"];
      toast: ToasterToast;
    }
  | {
      type: ActionType["UPDATE_TOAST"];
      toast: Partial<ToasterToast>;
    }
  | {
      type: ActionType["DISMISS_TOAST"];
      toastId?: ToasterToast["id"];
    }
  | {
      type: ActionType["REMOVE_TOAST"];
      toastId?: ToasterToast["id"];
    };

interface State {
  toasts: ToasterToast[];
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>();

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return;
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId);
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    });
  }, TOAST_REMOVE_DELAY);

  toastTimeouts.set(toastId, timeout);
};

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      };

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t,
        ),
      };

    case "DISMISS_TOAST": {
      const { toastId } = action;

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId);
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id);
        });
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t,
        ),
      };
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        };
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      };
  }
};

const listeners: Array<(state: State) => void> = [];

let memoryState: State = { toasts: [] };

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action);
  listeners.forEach((listener) => {
    listener(memoryState);
  });
}

type Toast = Omit<ToasterToast, "id">;

function toast({ ...props }: Toast) {
  const id = genId();

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    });
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id });

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss();
      },
    },
  });

  return {
    id: id,
    dismiss,
    update,
  };
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState);

  React.useEffect(() => {
    listeners.push(setState);
    return () => {
      const index = listeners.indexOf(setState);
      if (index > -1) {
        listeners.splice(index, 1);
      }
    };
  }, [state]);

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  };
}

export { useToast, toast };

import { useState, useEffect, useCallback } from 'react';

interface AdaptiveTheme {
  backgroundColor: string;
  textColor: string;
  accentColor: string;
  overlayColor: string;
  isLight: boolean;
  contrastRatio: number;
}

interface ColorAnalysis {
  dominant: string;
  vibrant: string;
  muted: string;
  lightness: number;
  saturation: number;
  isWarm: boolean;
}

export function useAdaptiveTheme(artworkUrl?: string) {
  const [adaptiveTheme, setAdaptiveTheme] = useState<AdaptiveTheme>({
    backgroundColor: 'rgba(0, 0, 0, 0.12)',
    textColor: '#ffffff',
    accentColor: '#f97316',
    overlayColor: 'rgba(0, 0, 0, 0.08)',
    isLight: false,
    contrastRatio: 21
  });

  const [isAnalyzing, setIsAnalyzing] = useState(false);

  // Convert hex to RGB
  const hexToRgb = (hex: string): [number, number, number] => {
    const result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
    return result ? [
      parseInt(result[1], 16),
      parseInt(result[2], 16),
      parseInt(result[3], 16)
    ] : [0, 0, 0];
  };

  // Calculate relative luminance for WCAG contrast
  const getRelativeLuminance = (r: number, g: number, b: number): number => {
    const rsRGB = r / 255;
    const gsRGB = g / 255;
    const bsRGB = b / 255;

    const rLinear = rsRGB <= 0.03928 ? rsRGB / 12.92 : Math.pow((rsRGB + 0.055) / 1.055, 2.4);
    const gLinear = gsRGB <= 0.03928 ? gsRGB / 12.92 : Math.pow((gsRGB + 0.055) / 1.055, 2.4);
    const bLinear = bsRGB <= 0.03928 ? bsRGB / 12.92 : Math.pow((bsRGB + 0.055) / 1.055, 2.4);

    return 0.2126 * rLinear + 0.7152 * gLinear + 0.0722 * bLinear;
  };

  // Calculate contrast ratio between two colors
  const getContrastRatio = (color1: [number, number, number], color2: [number, number, number]): number => {
    const l1 = getRelativeLuminance(color1[0], color1[1], color1[2]);
    const l2 = getRelativeLuminance(color2[0], color2[1], color2[2]);

    const lighter = Math.max(l1, l2);
    const darker = Math.min(l1, l2);

    return (lighter + 0.05) / (darker + 0.05);
  };

  // Get optimal text color for accessibility
  const getOptimalTextColor = (backgroundColor: [number, number, number]): string => {
    const whiteContrast = getContrastRatio(backgroundColor, [255, 255, 255]);
    const blackContrast = getContrastRatio(backgroundColor, [0, 0, 0]);

    // WCAG AAA requires 7:1 for normal text, AA requires 4.5:1
    if (whiteContrast >= 7) return '#ffffff';
    if (blackContrast >= 7) return '#000000';
    if (whiteContrast >= 4.5) return '#ffffff';
    if (blackContrast >= 4.5) return '#000000';

    // Fallback to highest contrast
    return whiteContrast > blackContrast ? '#ffffff' : '#000000';
  };

  // Analyze color properties
  const analyzeColor = (r: number, g: number, b: number): ColorAnalysis => {
    // Convert to HSL
    const rNorm = r / 255;
    const gNorm = g / 255;
    const bNorm = b / 255;

    const max = Math.max(rNorm, gNorm, bNorm);
    const min = Math.min(rNorm, gNorm, bNorm);
    const diff = max - min;

    const lightness = (max + min) / 2;
    const saturation = diff === 0 ? 0 : diff / (1 - Math.abs(2 * lightness - 1));

    // Determine if color is warm (red/orange/yellow dominant)
    const isWarm = r > b && (r > g || Math.abs(r - g) < 30);

    return {
      dominant: `rgb(${r}, ${g}, ${b})`,
      vibrant: `rgb(${Math.min(255, r * 1.2)}, ${Math.min(255, g * 1.1)}, ${Math.min(255, b * 1.1)})`,
      muted: `rgb(${Math.max(0, r * 0.7)}, ${Math.max(0, g * 0.7)}, ${Math.max(0, b * 0.7)})`,
      lightness,
      saturation,
      isWarm
    };
  };

  // Extract colors from image
  const extractColorsFromImage = useCallback(async (imageUrl: string): Promise<ColorAnalysis | null> => {
    return new Promise((resolve) => {
      try {
        const img = new Image();
        img.crossOrigin = 'anonymous';

        img.onload = () => {
          try {
            const canvas = document.createElement('canvas');
            const ctx = canvas.getContext('2d');

            if (!ctx) {
              resolve(null);
              return;
            }

            // Resize for performance
            const size = 50;
            canvas.width = size;
            canvas.height = size;

            ctx.drawImage(img, 0, 0, size, size);

            const imageData = ctx.getImageData(0, 0, size, size);
            const data = imageData.data;

            // Sample colors and find dominant
            const colorCounts: { [key: string]: number } = {};
            let totalR = 0, totalG = 0, totalB = 0;
            let pixelCount = 0;

            for (let i = 0; i < data.length; i += 16) { // Sample every 4th pixel
              const r = data[i];
              const g = data[i + 1];
              const b = data[i + 2];
              const a = data[i + 3];

              if (a > 128) { // Skip transparent pixels
                totalR += r;
                totalG += g;
                totalB += b;
                pixelCount++;

                // Group similar colors
                const colorKey = `${Math.floor(r / 32)}-${Math.floor(g / 32)}-${Math.floor(b / 32)}`;
                colorCounts[colorKey] = (colorCounts[colorKey] || 0) + 1;
              }
            }

            if (pixelCount === 0) {
              resolve(null);
              return;
            }

            // Calculate average color
            const avgR = Math.round(totalR / pixelCount);
            const avgG = Math.round(totalG / pixelCount);
            const avgB = Math.round(totalB / pixelCount);

            resolve(analyzeColor(avgR, avgG, avgB));

          } catch (error) {
            console.warn('Error analyzing image colors:', error);
            resolve(null);
          }
        };

        img.onerror = () => resolve(null);
        img.src = imageUrl;

      } catch (error) {
        console.warn('Error loading image for color analysis:', error);
        resolve(null);
      }
    });
  }, []);

  // Generate adaptive theme from color analysis
  const generateAdaptiveTheme = useCallback((colorAnalysis: ColorAnalysis): AdaptiveTheme => {
    const [r, g, b] = hexToRgb(colorAnalysis.dominant.replace('rgb(', '').replace(')', '').split(',').map(n => parseInt(n.trim())).map(n => n.toString(16).padStart(2, '0')).join(''));

    // Very low opacity for glass-like effect
    const backgroundOpacity = colorAnalysis.lightness > 0.6 ? 0.12 : 0.08;
    const backgroundColor = `rgba(${r}, ${g}, ${b}, ${backgroundOpacity})`;

    // Get optimal text color
    const textColor = getOptimalTextColor([r, g, b]);

    // Create accent color - more vibrant version
    const accentR = Math.min(255, Math.max(0, colorAnalysis.isWarm ? r * 1.2 : r * 1.1));
    const accentG = Math.min(255, Math.max(0, g * 1.1));
    const accentB = Math.min(255, Math.max(0, colorAnalysis.isWarm ? b * 0.8 : b * 1.2));
    const accentColor = `rgb(${Math.round(accentR)}, ${Math.round(accentG)}, ${Math.round(accentB)})`;

    // Create very subtle overlay for glass effect
    const overlayOpacity = colorAnalysis.lightness > 0.5 ? 0.08 : 0.05;
    const overlayColor = textColor === '#ffffff' 
      ? `rgba(0, 0, 0, ${overlayOpacity})`
      : `rgba(255, 255, 255, ${overlayOpacity})`;

    const contrastRatio = getContrastRatio([r, g, b], textColor === '#ffffff' ? [255, 255, 255] : [0, 0, 0]);

    return {
      backgroundColor,
      textColor,
      accentColor,
      overlayColor,
      isLight: colorAnalysis.lightness > 0.5,
      contrastRatio
    };
  }, []);

  // Main function to analyze artwork and set theme
  const analyzeArtwork = useCallback(async (artworkUrl: string) => {
    if (isAnalyzing) return;

    setIsAnalyzing(true);

    try {
      const colorAnalysis = await extractColorsFromImage(artworkUrl);

      if (colorAnalysis) {
        const newTheme = generateAdaptiveTheme(colorAnalysis);

        // Ensure minimum contrast ratio for accessibility
        if (newTheme.contrastRatio >= 4.5) {
          setAdaptiveTheme(newTheme);
        } else {
          // Fallback to high contrast glass theme
          setAdaptiveTheme({
            backgroundColor: 'rgba(0, 0, 0, 0.15)',
            textColor: '#ffffff',
            accentColor: colorAnalysis.isWarm ? '#f97316' : '#3b82f6',
            overlayColor: 'rgba(0, 0, 0, 0.08)',
            isLight: false,
            contrastRatio: 21
          });
        }
      }
    } catch (error) {
      console.warn('Error in adaptive theme analysis:', error);
    } finally {
      setIsAnalyzing(false);
    }
  }, [isAnalyzing, generateAdaptiveTheme, extractColorsFromImage]);

  // Effect to analyze artwork when URL changes
  useEffect(() => {
    if (artworkUrl && artworkUrl !== '' && artworkUrl !== 'advertisement') {
      // Add small delay to avoid too frequent updates
      const timeoutId = setTimeout(() => {
        analyzeArtwork(artworkUrl);
      }, 300);

      return () => clearTimeout(timeoutId);
    } else {
      // Reset to default glass theme
      setAdaptiveTheme({
        backgroundColor: 'rgba(0, 0, 0, 0.08)',
        textColor: '#ffffff',
        accentColor: '#f97316',
        overlayColor: 'rgba(0, 0, 0, 0.05)',
        isLight: false,
        contrastRatio: 21
      });
    }
  }, [artworkUrl, analyzeArtwork]);

  return {
    adaptiveTheme,
    isAnalyzing,
    analyzeArtwork
  };
}

import { useState, useCallback } from "react";
import { useAuth } from "@/contexts/AuthContext";
import { apiRequest } from "@/lib/queryClient";

export function useListeningStatus() {
  const [isActiveListening, setIsActiveListening] = useState(false);
  const [loading, setLoading] = useState(false);
  const { isAuthenticated } = useAuth();

  const updateListeningStatus = useCallback(
    async (status: boolean) => {
      if (!isAuthenticated) return;

      setLoading(true);

      try {
        await apiRequest("POST", "/api/user/listening-status", {
          isActiveListening: status,
        });

        setIsActiveListening(status);
      } catch (error) {
        console.error("Failed to update listening status:", error);
      } finally {
        setLoading(false);
      }
    },
    [isAuthenticated],
  );

  const startListening = useCallback(() => {
    updateListeningStatus(true);
  }, [updateListeningStatus]);

  const stopListening = useCallback(() => {
    updateListeningStatus(false);
  }, [updateListeningStatus]);

  return {
    isActiveListening,
    loading,
    startListening,
    stopListening,
    updateListeningStatus,
  };
}

import { useState, useEffect } from "react";
import { useAuth } from "@/contexts/AuthContext";
import { apiRequest } from "@/lib/queryClient";

interface LocationData {
  lat: number;
  lng: number;
  address?: string;
}

export function useLocation() {
  const [location, setLocation] = useState<LocationData | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const { isAuthenticated } = useAuth();

  const requestLocation = async () => {
    // Allow location detection even for unauthenticated users
    setLoading(true);
    setError(null);

    try {
      if (!navigator.geolocation) {
        throw new Error("Geolocation is not supported by this browser");
      }

      const position = await new Promise<GeolocationPosition>(
        (resolve, reject) => {
          navigator.geolocation.getCurrentPosition(resolve, reject, {
            enableHighAccuracy: true,
            timeout: 15000, // Increased timeout
            maximumAge: 60000, // Reduced cache time for more frequent updates
          });
        },
      );

      const locationData: LocationData = {
        lat: position.coords.latitude,
        lng: position.coords.longitude,
      };

      // Try to get address from coordinates (optional)
      try {
        const response = await fetch(
          `https://api.bigdatacloud.net/data/reverse-geocode-client?latitude=${locationData.lat}&longitude=${locationData.lng}&localityLanguage=en`,
        );

        if (response.ok) {
          const addressData = await response.json();
          locationData.address =
            `${addressData.city || addressData.locality || ""}, ${addressData.countryName || ""}`.trim();
        }
      } catch (addressError) {
        console.warn("Could not fetch address:", addressError);
      }

      setLocation(locationData);

      // Update location on server only if authenticated
      if (isAuthenticated) {
        try {
          await apiRequest("POST", "/api/user/location", {
            location: locationData,
          });
        } catch (apiError) {
          console.warn("Could not update location on server:", apiError);
        }
      }

      return locationData;
    } catch (err: any) {
      let errorMessage = "Failed to get location";

      if (err.code === 1) {
        errorMessage =
          "Location access denied. Please enable location services.";
      } else if (err.code === 2) {
        errorMessage = "Location unavailable. Please try again.";
      } else if (err.code === 3) {
        errorMessage = "Location request timed out. Please try again.";
      }

      setError(errorMessage);
      throw new Error(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  // Auto-request location on mount if authenticated
  useEffect(() => {
    if (isAuthenticated && !location && !loading) {
      requestLocation().catch(console.warn);
    }
  }, [isAuthenticated]);

  return {
    location,
    loading,
    error,
    requestLocation,
  };
}

