import React from 'react';
import { useTheme } from '../../contexts/ThemeContext';

interface FluidLoaderProps {
  size?: 'sm' | 'md' | 'lg';
  type?: 'pulse' | 'wave' | 'spin' | 'dots';
  color?: string;
}

const FluidLoader: React.FC<FluidLoaderProps> = ({
  size = 'md',
  type = 'pulse',
  color
}) => {
  const { colors } = useTheme();
  const loaderColor = color || colors.primary;

  const sizeClasses = {
    sm: 'w-6 h-6',
    md: 'w-12 h-12',
    lg: 'w-16 h-16'
  };

  const PulseLoader = () => (
    <div className={`relative ${sizeClasses[size]}`}>
      <div 
        className="absolute inset-0 rounded-full animate-ping"
        style={{ backgroundColor: `${loaderColor}40` }}
      />
      <div 
        className="absolute inset-2 rounded-full animate-pulse"
        style={{ backgroundColor: loaderColor }}
      />
    </div>
  );

  const WaveLoader = () => (
    <div className="flex space-x-1">
      {[0, 1, 2].map(i => (
        <div
          key={i}
          className={`${size === 'sm' ? 'w-1 h-4' : size === 'md' ? 'w-2 h-8' : 'w-3 h-12'} rounded-full animate-pulse`}
          style={{ 
            backgroundColor: loaderColor,
            animationDelay: `${i * 0.2}s`,
            animationDuration: '1s'
          }}
        />
      ))}
    </div>
  );

  const SpinLoader = () => (
    <div className={`relative ${sizeClasses[size]}`}>
      <div
        className="absolute inset-0 rounded-full border-2 border-transparent animate-spin"
        style={{
          borderTopColor: loaderColor,
          borderRightColor: `${loaderColor}60`
        }}
      />
    </div>
  );

  const DotsLoader = () => (
    <div className="flex space-x-2">
      {[0, 1, 2].map(i => (
        <div
          key={i}
          className={`${size === 'sm' ? 'w-2 h-2' : size === 'md' ? 'w-3 h-3' : 'w-4 h-4'} rounded-full animate-bounce`}
          style={{
            backgroundColor: loaderColor,
            animationDelay: `${i * 0.1}s`
          }}
        />
      ))}
    </div>
  );

  const loaders = {
    pulse: PulseLoader,
    wave: WaveLoader,
    spin: SpinLoader,
    dots: DotsLoader
  };

  const LoaderComponent = loaders[type];

  return (
    <div className="flex items-center justify-center p-4">
      <LoaderComponent />
    </div>
  );
};

export default FluidLoader;

import React, { useState, useEffect } from 'react';
import { useTheme } from '../../contexts/ThemeContext';
import FluidLoader from './FluidLoader';

interface GlobalPageLoaderProps {
  isLoading: boolean;
  children: React.ReactNode;
}

const GlobalPageLoader: React.FC<GlobalPageLoaderProps> = ({
  isLoading,
  children
}) => {
  const [showLoader, setShowLoader] = useState(isLoading);
  const [contentReady, setContentReady] = useState(!isLoading);
  const { colors, currentTheme } = useTheme();

  useEffect(() => {
    if (isLoading) {
      setShowLoader(true);
      setContentReady(false);
    } else {
      // Quick transition out of loading state
      setTimeout(() => {
        setContentReady(true);
        setTimeout(() => setShowLoader(false), 150);
      }, 200);
    }
  }, [isLoading]);

  if (showLoader) {
    return (
      <div 
        className="fixed inset-0 z-50 flex flex-col items-center justify-center transition-all duration-300"
        style={{ backgroundColor: colors.background }}
      >
        {/* Logo or Brand Element */}
        <div className="mb-8 scale-110 opacity-90 transition-all duration-500">
          <div 
            className="text-4xl font-black tracking-wider"
            style={{ color: colors.primary }}
          >
            SPANDEX SALVATION
          </div>
          <div 
            className="text-lg font-semibold text-center mt-2 opacity-70"
            style={{ color: colors.textSecondary }}
          >
            RADIO
          </div>
        </div>

        {/* Enhanced Loading Animation */}
        <FluidLoader size="lg" type="pulse" />

        {/* Loading Text */}
        <div 
          className="mt-6 text-sm font-medium opacity-60 animate-pulse"
          style={{ color: colors.textSecondary }}
        >
          Loading the ultimate metal experience...
        </div>

        {/* Progress Bar Effect */}
        <div className="mt-4 w-48 h-1 bg-gray-200 dark:bg-gray-700 rounded-full overflow-hidden">
          <div 
            className="h-full rounded-full transition-all duration-1000 ease-out"
            style={{ 
              width: contentReady ? '100%' : '60%',
              backgroundColor: colors.primary,
              transition: 'width 1s ease-out'
            }}
          />
        </div>
      </div>
    );
  }

  return (
    <div className={`transition-all duration-500 ease-out ${
      contentReady ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4'
    }`}>
      {children}
    </div>
  );
};

export default GlobalPageLoader;

import React, { useState, useEffect } from 'react';
import { useTheme } from '../../contexts/ThemeContext';

interface ImmersiveLoaderProps {
  isLoading: boolean;
  children: React.ReactNode;
  type?: 'fade' | 'slide' | 'scale' | 'blur';
  delay?: number;
  duration?: number;
}

const ImmersiveLoader: React.FC<ImmersiveLoaderProps> = ({
  isLoading,
  children,
  type = 'fade',
  delay = 0,
  duration = 300
}) => {
  const [hasLoaded, setHasLoaded] = useState(false);
  const [showContent, setShowContent] = useState(false);
  const { colors } = useTheme();

  useEffect(() => {
    if (!isLoading && !hasLoaded) {
      const timer = setTimeout(() => {
        setHasLoaded(true);
        // Small additional delay for smooth transition
        setTimeout(() => setShowContent(true), 50);
      }, delay);
      return () => clearTimeout(timer);
    }
  }, [isLoading, hasLoaded, delay]);

  const getTransitionClasses = () => {
    const baseClasses = `transition-all duration-${duration} ease-out`;

    if (!hasLoaded || isLoading) {
      switch (type) {
        case 'slide':
          return `${baseClasses} transform translate-y-4 opacity-0`;
        case 'scale':
          return `${baseClasses} transform scale-95 opacity-0`;
        case 'blur':
          return `${baseClasses} filter blur-sm opacity-0`;
        default:
          return `${baseClasses} opacity-0`;
      }
    }

    return `${baseClasses} transform translate-y-0 scale-100 opacity-100 filter blur-none`;
  };

  const Skeleton = () => (
    <div className="animate-pulse space-y-4">
      <div 
        className="h-6 rounded-lg"
        style={{ backgroundColor: `${colors.surface}40` }}
      />
      <div 
        className="h-4 rounded-lg w-3/4"
        style={{ backgroundColor: `${colors.surface}30` }}
      />
      <div 
        className="h-4 rounded-lg w-1/2"
        style={{ backgroundColor: `${colors.surface}20` }}
      />
    </div>
  );

  return (
    <div className="relative">
      {isLoading && (
        <div className="absolute inset-0 z-10 flex items-center justify-center">
          <Skeleton />
        </div>
      )}

      <div className={getTransitionClasses()}>
        {children}
      </div>
    </div>
  );
};

export default ImmersiveLoader;

import React, { useState, useEffect, useRef } from 'react';
import { useTheme } from '../../contexts/ThemeContext';

interface PageTransitionProps {
  children: React.ReactNode;
  className?: string;
}

const PageTransition: React.FC<PageTransitionProps> = ({
  children,
  className = ''
}) => {
  const [isVisible, setIsVisible] = useState(false);
  const [hasAnimated, setHasAnimated] = useState(false);
  const elementRef = useRef<HTMLDivElement>(null);
  const { colors } = useTheme();

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting && !hasAnimated) {
          setIsVisible(true);
          setHasAnimated(true);
        }
      },
      {
        threshold: 0.1,
        rootMargin: '50px'
      }
    );

    if (elementRef.current) {
      observer.observe(elementRef.current);
    }

    return () => observer.disconnect();
  }, [hasAnimated]);

  return (
    <div
      ref={elementRef}
      className={`transform transition-all duration-500 ease-out ${
        isVisible 
          ? 'translate-y-0 opacity-100 scale-100' 
          : 'translate-y-8 opacity-0 scale-95'
      } ${className}`}
    >
      {children}
    </div>
  );
};

export default PageTransition;

import React, { useState, useEffect, useRef } from 'react';

interface StaggeredRevealProps {
  children: React.ReactNode[];
  delay?: number;
  staggerDelay?: number;
  className?: string;
}

const StaggeredReveal: React.FC<StaggeredRevealProps> = ({
  children,
  delay = 0,
  staggerDelay = 100,
  className = ''
}) => {
  const [visibleItems, setVisibleItems] = useState<boolean[]>([]);
  const [hasStarted, setHasStarted] = useState(false);
  const containerRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting && !hasStarted) {
          setHasStarted(true);

          // Initialize visibility array
          setVisibleItems(new Array(children?.length || 0).fill(false));

          // Stagger the animations
          children.forEach((_, index) => {
            setTimeout(() => {
              setVisibleItems(prev => {
                const newState = [...prev];
                newState[index] = true;
                return newState;
              });
            }, delay + (index * staggerDelay));
          });
        }
      },
      {
        threshold: 0.1,
        rootMargin: '30px'
      }
    );

    if (containerRef.current) {
      observer.observe(containerRef.current);
    }

    return () => observer.disconnect();
  }, [children?.length || 0, delay, staggerDelay, hasStarted]);

  return (
    <div ref={containerRef} className={className}>
      {children.map((child, index) => (
        <div
          key={index}
          className={`transform transition-all duration-400 ease-out ${
            visibleItems[index]
              ? 'translate-y-0 opacity-100 scale-100'
              : 'translate-y-6 opacity-0 scale-95'
          }`}
        >
          {child}
        </div>
      ))}
    </div>
  );
};

export default StaggeredReveal;

